SELECT * FROM (
  SELECT a.*,ROWNUM ROW_NUM FROM (
    SELECT ID,NAME,PUBLISHED_AT,LAST_ARTICLE_TYPE_ID FROM T_ISS_PS_ARTICLE
    WHERE STATUS = 2
    AND IS_DELETED = 0
    AND ARTICLE_TYPE_ID = #{articleTypeId}
    ORDER BY PUBLISHED_AT DESC) a
  ) b 
WHERE b.ROW_NUM between #{start} AND #{end}

SELECT * FROM ( SELECT a.*,ROWNUM ROW_NUM FROM ( SELECT ID,NAME,PUBLISHED_AT,LAST_ARTICLE_TYPE_ID FROM T_ISS_PS_ARTICLE WHERE IS_DELETED =0
AND NAME LIKE CONCAT(CONCAT('%', #{title}), '%')
AND STATUS=2 ORDER BY PUBLISHED_AT DESC) a ) b WHERE b.ROW_NUM between #{start} AND #{end}
