mapper.select:
    select PRODUCT.ID id,PRODUCT.NAME productName,projectResult.RESULT_COUNT productNum,projectResult.OFFER_PRICE myOfferMoney
  	  FROM T_BSS_OB_PRODUCT product,T_BSS_OB_PROJECT_RESULT projectResult 
		where projectResult.PROJECT_ID=#{projectId} AND projectResult.SUPPLIER_ID=#{supplierId} 
		  AND PRODUCT.ID=projectResult.PRODUCT_ID;

select status from T_BSS_OB_PROJECT_RESULT 
WHERE PROJECT_ID=#{projectId} AND SUPPLIER_ID=#{supplierId} 
GROUP BY STATUS

select 

from T_BSS_OB_PROJECT_RESULT
where 
PROJECT_ID = #{id,jdbcType=VARCHAR}

  AND STATUS =#{status,jdbcType=DECIMAL} 

order by RANKING

    SELECT distinct obProject.NAME QUOTENAME,obProjectResult.RANKING RANKING,obProjectResult.PROPORTION bidRatio,OBPROJECT.START_TIME CONFIRMOVERTIME,ruleTable.CONFIRM_TIME confirmTime,ruleTable.CONFIRM_TIME_SECOND confirmTimeSecond,ruleTable.QUOTE_TIME quoteTime 
	  FROM T_BSS_OB_PROJECT obProject,T_BSS_OB_PROJECT_RESULT obProjectResult,(select QUOTE_TIME,CONFIRM_TIME,CONFIRM_TIME_SECOND from T_BSS_OB_RULE where id=(select DISTINCT RULE_ID FROM T_BSS_OB_PROJECT WHERE id=#{projectId,jdbcType=VARCHAR})) ruleTable
		WHERE obProjectResult.PROJECT_ID=obProject.ID AND obProjectResult.SUPPLIER_ID=#{supplierId,jdbcType=VARCHAR} AND OBPROJECTRESULT.PROJECT_ID=#{projectId,jdbcType=VARCHAR}
