mapper.select:
 select 

from T_BSS_PMS_AUDIT_PERSON

where COLLECT_ID = #{collectId} 

and AUDIT_ROUND = #{auditRound}

  	SELECT count(1)
		FROM T_BSS_PMS_AUDIT_PERSON
		WHERE AUDIT_ROUND = #{auditRound}
		AND COLLECT_ID = #{collectId}
		AND USER_ID = #{userId}

SELECT 

from T_BSS_PMS_AUDIT_PERSON
where 1=1

 and COLLECT_ID = #{collectId} 


  and TYPE = #{type}

ORDER BY CREATE_DATE ASC

SELECT 

from T_BSS_PMS_AUDIT_PERSON
where 1=1

 and COLLECT_ID = #{collectId} 


AND USER_ID = #{userId}

ORDER BY CREATE_DATE ASC

mapper.update:
