mapper.select:
  
  select 
  
  from T_BSS_PMS_COLLECT_PLAN
  	 1=1
     
       and  FILE_NAME like CONCAT(CONCAT('%', #{fileName}), '%') 
      
  	  
       and  BUDGET=#{budget}
      
       
        and PURCHASE_TYPE = #{purchaseType}
      
      
      
       and  USER_ID=#{userId}
      
       
         and  PLAN_NUMBER = #{planNo}
      
      
	    
	  	   and status in(1,2,3,4,5,6,7,12)
	  	
       
	  	   and GOODS_TYPE = #{goodsType}
	   
	  	 
	  	    and AUDIT_TURN in (1,2,3) and  STATUS in (2,12) 
	   
	  
	   
	  	    and status in(1,3,4,5,6,7,12)
	   
	   
	  	    and status in(1,3,4,5,6,7,12)
	   
	   
	   
	  	    and AUDIT_TURN is null and  STATUS in (2,12) 
	   
	   
	   
	    
	  	   and status=#{status}
	  	
	  	
  	
  	
   order by CREATED_AT desc

 
 select 
 
 from T_BSS_PMS_COLLECT_PLAN
    1=1
    
      and  FILE_NAME like CONCAT(CONCAT('%', #{fileName}), '%') 
     
     
      and  BUDGET like CONCAT(CONCAT('%', #{budget}), '%') 
     
      
       and PURCHASE_TYPE = #{purchaseType}
     
     
     
      and  USER_ID=#{userId}
     
      
        and  PLAN_NUMBER = #{planNo}
     
     
     
        and status in(1,2,3,4,5,6,7,12)
     
      
        and GOODS_TYPE = #{goodsType}
    
      
         and AUDIT_TURN in (1,2,3) and  STATUS in (2,12) 
    
   
    
         and status in(1,3,4,5,6,7,12)
    
    
         and status in(1,3,4,5,6,7,12)
    
    
    
         and AUDIT_TURN is null and  STATUS in (2,12) 
    
    
    
     
        and status=#{status}
     
     
   
   
  order by CREATED_AT desc

select
from T_BSS_PMS_COLLECT_PLAN 1=1
and FILE_NAME like CONCAT(CONCAT('%', #{fileName}), '%')
and BUDGET=#{budget}
and PURCHASE_TYPE = #{purchaseType}
and PLAN_NUMBER = #{planNo}
and status in(1,2,3,4,5,6,7,12)
and GOODS_TYPE = #{goodsType}
and AUDIT_TURN in (1,2,3) and STATUS in (2,12)
and status in(1,3,4,5,6,7,12)
and status in(1,3,4,5,6,7,12)
and AUDIT_TURN is null and STATUS in (2,12)
and status=#{status}
and USER_ID in( select id from T_SES_BMS_USER WHERE org_id=(select org_id from T_SES_BMS_USER where id=#{userId}) UNION select id from T_SES_BMS_USER where org_id in (select PURCHASE_DEP_ID from T_SES_OMS_PURDEP_ORG where ORG_ID=(select org_id from T_SES_BMS_USER where id=#{userId})))
order by CREATED_AT desc

SELECT * FROM ( SELECT c.FILE_NAME, c. ID, c.BUDGET, c.STATUS, T .GIVE_TIME createdAt, T .COLLECT_ID FROM T_BSS_PMS_COLLECT_PLAN c LEFT JOIN T_BSS_PPMS_TASK T ON c."ID" = T .COLLECT_ID WHERE T .TASK_NATURE = 0 AND T .IS_DELETED = 0 AND c.STATUS = 2
and to_char(T.GIVE_TIME,'yyyy') = #{date}
and c.FILE_NAME like CONCAT(CONCAT('%', #{fileName}), '%')
) T GROUP BY T .FILE_NAME, T .createdAt, T .COLLECT_ID, t.STATUS, T . ID, T .BUDGET ORDER BY T .createdAt DESC

SELECT o.SHORT_NAME as taskId, c .id, c.FILE_NAME, NVL (SUM(D .BUDGET), 0) AS BUDGET, c.CREATED_AT,c.STATUS FROM T_BSS_PMS_PURCHASE_DETAIL D LEFT JOIN T_SES_OMS_ORG o ON o. ID = D .ORGANIZATION_ID LEFT JOIN T_BSS_PMS_COLLECT_PLAN c ON c. ID = D .UNIQUE_ID WHERE o. ID = #{orgnizationId}
and c.FILE_NAME like CONCAT(CONCAT('%', #{fileName}), '%')
AND c.STATUS = 2 AND D .PRICE IS NOT NULL AND D .HISTORY_STATUS = '0' AND o.IS_DELETED = 0 GROUP BY o.SHORT_NAME, c .id,c.CREATED_AT, c.FILE_NAME,c.STATUS ORDER BY BUDGET DESC
