				and IS_MASTER = #{isMaster}
			
			
				and STATUS = #{status}
			
			
			
                and PARENT_ID = #{parentId}
            
            
		
		ORDER BY IS_MASTER

sql.where:
 IS_DELETE!='1' and PURCHASE_COUNT is not null
 and IS_MASTER!='1' and HISTORY_STATUS='0'
 	
			and PLAN_TYPE = #{planType}
	
 	
 	and to_char(CREATED_AT,'yyyy')=#{year}
 	
 	
