mapper.sql:
where aq.IS_DELETED = 0 

  
    and aq.ID = #{id} 
  
  
    and aq.PROJECT_ID = #{projectId} 
  
  
    and aq.PACKAGE_ID = #{packageId} 
  
  
    and aq.SUPPLIER_ID = #{supplierId} 
  
  
    and aq.SCORE_MODEL_ID = #{scoreModelId} 
  
  
    and aq.SUPPLIER_VALUE = #{supplierValue} 
  
  
    and aq.INIT_SCORE = #{initScore} 
  
  
    and aq.EXPERT_VALUE = #{expertValue} 
  
  
    and aq.FINAL_SCORE = #{finalScore} 
  
  
    and aq.ROUND = #{round} 
  
  
    and aq.CREATED_AT = #{createdAt} 
  
  
    and aq.UPDATED_AT = #{updatedAt} 
  
  
    and aq.PAGE = #{page}

mapper.select:
select q.ID AS quotaId,q.PROJECT_ID AS projectId,q.PACKAGE_ID AS packageId,q.SUPPLIER_ID AS supplierId, q.SCORE_MODEL_ID AS scoreModelId,q.SUPPLIER_VALUE AS supplierValue,q.INIT_SCORE AS initScore, q.EXPERT_VALUE AS expertValue,q.FINAL_SCORE AS finalScore,q.CREATED_AT AS createdAt, q.UPDATED_AT AS updatedAt,q.IS_DELETED AS isDeleted,q.ROUND AS round,q.PAGE AS page,
s.MARK_TERM_ID AS markTermId,s.NAME AS name,s.TYPE_NAME AS typeName,s.REVIEW_CONTENT AS reviewContent, s.REMARK AS remark,s.EASY_UNDERSTAND_CONTENT AS easyUnderstandContent,s.STANDARD_EXPLAIN AS standExplain, s.STANDARD_SCORE AS standardScore,s.JUDGE_CONTENT AS judgeContent,s.REVIEW_PARAM AS reviewParam, s.ADD_SUBTRACT_TYPE_NAME AS addSubtractTypeName,s.UNIT_SCORE AS unitScore,s.UNIT AS unit,s.REVIEW_STAND_SCORE AS reviewStandScore, s.MAX_SCORE AS maxScore,s.MIN_SCORE AS minScore,s.SCORE AS score,s.INTERVAL_TYPE_NAME AS intervalTypeName, s.DEADLINE_NUMBER AS deadlineNumber,s.INTERVAL_NUMBER AS intervalNumber, mt.NAME AS markTermName from T_BSS_PPMS_AUDIT_QUOTA q LEFT JOIN T_BSS_PPMS_SCORE_MODEL s ON q.SCORE_MODEL_ID = s.ID LEFT JOIN T_BSS_PPMS_MARK_TERM mt ON s.MARK_TERM_ID = mt.ID
where 1=1 and q.IS_DELETED != 1
AND q.PACKAGE_ID = #{packageId}
AND q.PROJECT_ID=#{projectId}
AND mt.TYPE_NAME=#{typeName}
