mapper.select:
SELECT mt.id as id, mt.NAME as name, mt.TYPE_NAME as typeName, mt.MAX_SCORE as maxScore, mt.IS_DELETED as isDeleted, mt.CREATED_AT as createdAt, mt.UPDATED_AT as updatedAt, mt.PROJECT_ID as projectId, mt.FLOATING_RATIO as floatingRatio, mt.PACKAGE_ID as packageId, mt.REMARK as remark, mt.REMAIN_SCORE as remainScore, mt.business, mt.valid, t.TYPE_NAME as type FROM T_BSS_PPMS_BID_METHOD mt left join T_BSS_PPMS_MARK_TERM t on mt.id = t.bid_method_id WHERE nvl(mt.is_deleted,0)=0
and mt.id = #{id}
and mt.PROJECT_ID = #{projectId}
and mt.PACKAGE_ID = #{packageId}
and mt.TYPE_NAME = #{typeName}
and mt.NAME = #{name}
and t.type_name = #{type}
ORDER BY mt.CREATED_AT DESC

SELECT mt.id, mt.FLOATING_RATIO as floatingRatio, mt.type_name as typeName, mt.business, mt.valid FROM T_BSS_PPMS_BID_METHOD mt WHERE nvl(mt.is_deleted,0)=0 and type_name is not null
and mt.PROJECT_ID = #{projectId}
and mt.PACKAGE_ID = #{packageId}

SELECT mt.id as id, mt.NAME as name, mt.TYPE_NAME as typeName, mt.MAX_SCORE as maxScore, mt.IS_DELETED as isDeleted, mt.CREATED_AT as createdAt, mt.UPDATED_AT as updatedAt, mt.PROJECT_ID as projectId, mt.FLOATING_RATIO as floatingRatio, mt.PACKAGE_ID as packageId, mt.REMARK as remark, mt.REMAIN_SCORE as remainScore, mt.business, mt.valid FROM T_BSS_PPMS_BID_METHOD mt where 1=1
and mt.PACKAGE_ID = #{packageId}
and mt.PROJECT_ID = #{projectId}

mapper.update:
update T_BSS_PPMS_BID_METHOD
set
  updated_at = sysdate

    ,NAME = #{name}


    ,TYPE_NAME = #{typeName}


    ,MAX_SCORE = #{maxScore}


    ,is_deleted = #{isDeleted}


    ,PROJECT_ID = #{projectId}


    ,FLOATING_RATIO = #{floatingRatio}


    ,REMARK = #{remark}


    ,REMAIN_SCORE = #{remainScore}


    ,VALID = #{valid}


    ,BUSINESS = #{business}


    ,PACKAGE_ID = #{packageId}

where ID = #{id}

mapper.delete:
		delete from T_BSS_PPMS_BID_METHOD s where 1=1 and type_name is null

         	and PROJECT_ID = #{projectId}


         	and PACKAGE_ID = #{packageId}


         	and id= #{id}
