mapper.sql:
where fd.IS_DELETED = 0 


    and fd.ID = #{id} 


    and fd.NAME  like CONCAT(CONCAT('%', #{name}), '%') 


    and fd.STEP = #{step} 


    and fd.CREATED_AT = #{createdAt} 


    and fd.UPDATED_AT = #{updatedAt} 


    and fd.PURCHASE_TYPE_ID = #{purchaseTypeId} 


    and fd.URL = #{url} 


    and fd.CODE = #{code} 


    and fd.ADVANCED_URL = #{advancedUrl}
