mapper.select:
SELECT mt.id as id, mt.PID as pid, mt.NAME as name, mt.PNAME as pname, mt.URL as url, mt.CHECKED as checked, mt.IS_DELETED as isDeleted, mt.CREATED_AT as createdAt, mt.UPDATED_AT as updatedAt, mt.MAX_SCORE as maxScore, mt.PROJECT_ID as projectId, mt.PACKAGE_ID as packageId, mt.REMAIN_SCORE as remainScore, mt.TYPE_NAME as typeName, b.id as bidMethodId, b.name as bidMethodName, b.TYPE_NAME as bidMethodTypeName, b.MAX_SCORE as bidMethodMaxScore, b.FLOATING_RATIO as bidMethodFloatingRatio, b.REMARK as bidMethodRemark, b.REMAIN_SCORE as position, sm.name as smname, sm.EASY_UNDERSTAND_CONTENT as smeu, nvl(sm.standard_score ,sm.max_score) as scscore, sm.type_name as smtypename, sm.id as smId FROM T_BSS_PPMS_MARK_TERM mt left join T_BSS_PPMS_BID_METHOD b on MT.BID_METHOD_ID = b.id left join T_BSS_PPMS_SCORE_MODEL sm on sm.mark_term_id = mt.id WHERE nvl(mt.is_deleted,0)=0
and mt.id = #{id}
and mt.pid = #{pid}
and mt.name = #{name}
and mt.TYPE_NAME = #{typeName}
and mt.pname = #{pname}
and mt.pid is null
and mt.PROJECT_ID =#{projectId}
and mt.PACKAGE_ID =#{packageId}
and mt.BID_METHOD_ID =#{bidMethodId}
and mt.CHECKED =#{checked}
ORDER BY mt.CREATED_AT ASC, b.REMAIN_SCORE ASC

SELECT mt.id as id, mt.PID as pid, mt.NAME as name, mt.PNAME as pname, mt.URL as url, mt.CHECKED as checked, mt.IS_DELETED as isDeleted, mt.CREATED_AT as createdAt, mt.UPDATED_AT as updatedAt, mt.MAX_SCORE as maxScore, mt.PROJECT_ID as projectId, mt.PACKAGE_ID as packageId, mt.REMAIN_SCORE as remainScore, mt.TYPE_NAME as typeName, b.id as bidMethodId, b.name as bidMethodName, b.TYPE_NAME as bidMethodTypeName, b.MAX_SCORE as bidMethodMaxScore, b.FLOATING_RATIO as bidMethodFloatingRatio, b.REMARK as bidMethodRemark FROM T_BSS_PPMS_MARK_TERM mt left join T_BSS_PPMS_BID_METHOD b on MT.BID_METHOD_ID = b.id WHERE nvl(mt.is_deleted,0)=0
and mt.id= #{_parameter}
ORDER BY mt.CREATED_AT DESC

mapper.update:
update T_BSS_PPMS_MARK_TERM
set
  updated_at = sysdate

    ,pid = #{pid}


    ,name = #{name}


    ,pname = #{pname}


    ,is_deleted = #{isDeleted}


    ,BID_METHOD_ID = #{bidMethodId}


    ,MAX_SCORE = #{maxScore}


    ,PROJECT_ID = #{projectId}


    ,PACKAGE_ID = #{packageId}


    ,REMAIN_SCORE = #{remainScore}


    ,TYPE_NAME = #{typeName}


    ,CHECKED = #{checked}

where ID = #{id}

mapper.delete:
		delete from T_BSS_PPMS_MARK_TERM s where 1=1

         	and pid is not null


         	and PROJECT_ID = #{projectId}


         	and PACKAGE_ID = #{packageId}


         	and id= #{id}
