mapper.sql:
where 


     p.ID = #{id} 


    and p.NAME = #{name} 


    and p.PROJECT_NUMBER = #{projectNumber} 


    and p.STATUS = #{status} 


    and p.PRINCIPAL = #{principal}


    and p.IPONE = #{ipone} 


    and p.LINKMAN = #{linkman}


    and p.LINKMAN_IPONE = #{linkmanIpone}


    and p.BID_UNIT = #{bidUnit}


    and p.ADDRESS = #{address}


    and p.POSTCODE= #{postcode}


    and p.SUPPLIER_NUMBER = #{supplierNumber} 


    and p.OFFER_STANDARD = #{offerStandard} 


    and p.PR_INTRODUCE = #{prIntroduce} 


    and p.BUDGET_AMOUNT = #{budgetAmount}


    and p.PASS_WORD = #{passWord}


    and p.SCORING_RUBRIC = #{scoringRubric}


    and p.OPERATOR= #{operator}


    and p.DIVISION_OF_WORK = #{divisionOfWork} 


    and p.PURCHASE_TYPE = #{purchaseType} 


    and p.MATERIALS_TYPE = #{materialsType} 


    and p.SECTOR_OF_DEMAND = #{sectorOfDemand}


    and p.PURCHASE_DEP_ID = #{purchaseDep.id}


    and p.DEADLINE = #{deadline,jdbcType=TIMESTAMP} 


    and p.DATE_OF_ENTRUSTMENT = #{dateOfEntrustment} 


    and p.BID_DATE = #{bidDate,jdbcType=TIMESTAMP} 


    and p.BID_ADDRESS = #{bidAddress}


    and p.REQUIERED_ID = #{requieredId}


    and p.BALE_NO = #{baleNo} 


    and p.AMOUNT = #{amount} 


    and p.DEAL_SUPPLIER = #{purchaseDepName} 


    and p.PURCHASE_DEP_NAME = #{dealSupplier.id}


    and p.EXECUTE_STATUS = #{executeStatus}


    and p.IS_ENTRANCE = #{isEntrance}

mapper.select:
SELECT  FROM 
 (SELECT DISTINCT project_id FROM T_BSS_PPMS_SALE_TENDER  
 IS_REMOVED=0

     AND supplier_id = #{supplierId}

 ) t2
  LEFT JOIN
 T_BSS_PPMS_PROJECTS t1 ON  t1.id=t2.project_id
  t1.IS_PROVISIONAL=0

     AND t1.name = #{name}


     AND t1.PROJECT_NUMBER = #{projectNumber}

select
from T_BSS_PPMS_PROJECTS where 1=1 and IS_REHEARSE = 0 and IS_PROVISIONAL=0
and NAME = #{name}
and PROJECT_NUMBER = #{projectNumber}
and STATUS like CONCAT(CONCAT('%', #{status}), '%')
ORDER BY CREATED_AT DESC

select

from T_BSS_PPMS_PROJECTS
where 1=1 

    and NAME = #{name}


    and PROJECT_NUMBER = #{projectNumber}

ORDER BY CREATED_AT DESC

SELECT

FROM
  T_BSS_PPMS_PROJECTS
WHERE 1=1 and IS_REHEARSE = 0 and STATUS != '4'


  and  PURCHASE_DEP_ID = #{purchaseDepId}



 and NAME like CONCAT(CONCAT('%', #{name}), '%')


 and PROJECT_NUMBER like CONCAT(CONCAT('%', #{projectNumber}), '%')


 and STATUS = #{status}


 and PURCHASE_TYPE = #{purchaseType}


ORDER BY CREATED_AT DESC

SELECT p.* FROM T_BSS_PPMS_PROJECTS p WHERE p.ID IN (SELECT pt.PROJECT_ID FROM T_BSS_PPMS_PROJECT_TASK pt WHERE pt.TASK_ID IN ( SELECT t . ID FROM T_BSS_PPMS_TASK t WHERE
t.ORG_ID = #{orgId}
) ) and IS_REHEARSE = 0
and p.NAME like CONCAT(CONCAT('%', #{name}), '%')
and p.PROJECT_NUMBER like CONCAT(CONCAT('%', #{projectNumber}), '%')
and p.STATUS = #{status}
ORDER BY p.CREATED_AT DESC

SELECT p.* FROM T_BSS_PPMS_PROJECTS p WHERE ( p.ID IN (SELECT e.PROJECT_ID FROM T_BSS_PPMS_FLOW_EXECUTE e WHERE
e.OPERATOR_ID = #{userId}
AND e.IS_DELETED = 0 AND e.STATUS = 0)
OR (p.PRINCIPAL = #{principal}) )
AND p.IS_REHEARSE = 0
AND p.PURCHASE_DEP_ID = #{purchaseDepId}
AND p.NAME like CONCAT(CONCAT('%', #{name}), '%')
AND p.PROJECT_NUMBER like CONCAT(CONCAT('%', #{projectNumber}), '%')
AND p.STATUS = #{status}
ORDER BY p.CREATED_AT DESC

SELECT p.NAME, p.PURCHASE_TYPE,p.STATUS,p.PRINCIPAL,p.CREATED_AT,p.PROJECT_NUMBER FROM T_BSS_PPMS_PROJECTS p WHERE p.STATUS != '4' AND p.IS_REHEARSE = 0 AND p.ID IN ( SELECT CP.PROJECT_ID FROM ( SELECT T .PROJECT_ID, T .CONTRACT_ID FROM T_BSS_PPMS_SUPPLIER_CHECK_PASS T WHERE T .IS_WON_BID = 1 GROUP BY T .PROJECT_ID, T .CONTRACT_ID ) CP LEFT JOIN T_BSS_CS_PURCHASE_CONTRACT PC ON CP.CONTRACT_ID = PC. ID WHERE PC.MANUAL_TYPE = 0 )
and p.NAME like CONCAT(CONCAT('%', #{name}), '%')
and p.PROJECT_NUMBER like CONCAT(CONCAT('%', #{projectNumber}), '%')
and p.STATUS = #{status}
and p.PURCHASE_TYPE = #{purchaseType}
ORDER BY p.CREATED_AT DESC

SELECT p.* FROM ( SELECT d.PROJECT_ID FROM T_BSS_PPMS_PROJECTS_DETAIL d WHERE d.DEPARTMENT IN ( SELECT o. NAME FROM T_SES_OMS_ORG o LEFT JOIN T_SES_BMS_USER u ON o. ID = u .ORG_ID WHERE
u.ID = #{userId}
) GROUP BY d.PROJECT_ID ) p LEFT JOIN T_BSS_PPMS_PROJECTS ps ON p.PROJECT_ID = ps.ID
AND p.NAME like CONCAT(CONCAT('%', #{name}), '%')
AND p.PROJECT_NUMBER like CONCAT(CONCAT('%', #{projectNumber}), '%')
AND p.STATUS = #{status}
ORDER BY p.CREATED_AT DESC
