mapper.select:
SELECT

FROM
T_BSS_PPMS_SUPPLIER_CHECK_PASS cp
WHERE
1=1

	AND CP.ID =#{id}


	AND CP.PACKAGE_ID =#{packageId}


  AND CP.PROJECT_ID =#{projectId}


	AND CP.SUPPLIER_ID =#{supplierId}


	AND IS_SEND_NOTICE=#{isSendNotice}


	AND IS_WON_BID = #{isWonBid}


ORDER BY
RANKING

	SELECT

	FROM
	T_BSS_PPMS_SUPPLIER_CHECK_PASS cp
	WHERE
	1=1

		AND CP.ID =#{id}


      AND CP.PROJECT_ID =#{projectId}


		AND CP.PACKAGE_ID =#{packageId}


		AND CP.SUPPLIER_ID in ${supplierId}


		AND IS_SEND_NOTICE=#{isSendNotice}


		AND IS_WON_BID = #{isWonBid}


	ORDER BY
	RANKING

SELECT
FROM T_BSS_PPMS_SUPPLIER_CHECK_PASS cp, T_BSS_PPMS_PROJECTS TP WHERE cp.PROJECT_ID=TP.ID
AND CP.IS_WON_BID = #{isWonBid}
AND TP.PURCHASE_DEP_ID = #{purchaseDepId}
AND TP.name like '%'||#{projectName}||'%'
AND TP.project_Number like '%'||#{projectCode}||'%'
AND CP.IS_CREATE_CONTRACT=#{isCreateContract}
ORDER BY CP.IS_CREATE_CONTRACT
