mapper.select:
select 
*
from T_BSS_PPMS_TASK
where  STATUS = 0

   and  ID = #{id}


   and  NAME = #{name}


   and  STATUS = #{status}


    and PROCUREMENT_METHOD = #{procurementMethod} 


    and YEAR like '%${year}%'


    and GIVE_TIME =  #{giveTime}


    and PURCHASE_ID like '%${purchaseId}%'


    and DOCUMENT_NUMBER like '%${documentNumber}%'


    and PURCHASE_REQUIRED_ID = #{purchaseRequiredId}


    and MATERIALS_TYPE = #{materialsType}

 ORDER BY GIVE_TIME DESC

select t.ID as id, t.NAME as name, t.PURCHASE_ID as purchaseId, t.PURCHASE_REQUIRED_ID as purchaseRequiredId, t.STATUS as status, t.DOCUMENT_NUMBER as documentNumber, t.TASK_NATURE as taskNature, t.GIVE_TIME as giveTime, t.MATERIALS_TYPE as materialsType, o.NAME as orgName from T_BSS_PPMS_TASK t left join T_SES_OMS_ORG o on t.PURCHASE_REQUIRED_ID = o.ID where 1=1 and t.IS_DELETED = 0
and o.NAME like CONCAT(CONCAT('%', #{name}), '%')
and t.DOCUMENT_NUMBER like CONCAT(CONCAT('%', #{documentNumber}), '%')
and t.STATUS = #{status}
and t.TASK_NATURE = #{taskNature}
ORDER BY STATUS ASC,GIVE_TIME DESC

select 
*
from T_BSS_PPMS_TASK
where  IS_DELETED = 0 

   and  ID = #{id}


