mapper.insert:
insert into T_BSS_PPMS_TASK (ID, NAME, 
  PURCHASE_ID, DOCUMENT_NUMBER, GIVE_TIME, STATUS, PROCUREMENT_METHOD, PURCHASE_REQUIRED_ID,
  )
values (#{id}, #{name}, 
  #{purchaseId}, #{documentNumber}, #{giveTime}, #{status}, #{procurementMethod}, #{purchaseRequiredId},
  )
