mapper.select:
select
from T_BSS_PQIMS_PQINFO where 1=1
and CONTRACT_ID = #{contract.id}

select * from T_BSS_PQIMS_PQINFO p LEFT JOIN T_BSS_CS_PURCHASE_CONTRACT c on p.CONTRACT_ID = c.id where 1=1
AND p.CONCLUSION=#{conclusion}
AND p.TYPE=#{type}
AND c.CODE LIKE CONCAT(CONCAT('%', #{contract.code}), '%')
AND c.NAME LIKE CONCAT(CONCAT('%', #{contract.name}), '%')
ORDER BY p.CREATED_AT DESC
