mapper.select:
SELECT 

FROM ${tableName}
WHERE IS_DELETED = 0

	AND BUSINESS_ID = #{businessId,jdbcType=VARCHAR}


	AND TYPE_ID = #{typeId,jdbcType=VARCHAR}

SELECT 

FROM ${tableName}
WHERE IS_DELETED = 1

 AND BUSINESS_ID = #{businessId,jdbcType=VARCHAR}


 AND TYPE_ID = #{typeId,jdbcType=VARCHAR}

SELECT 

FROM ${tableName}
WHERE IS_DELETED = 0 AND ID = #{id,jdbcType=VARCHAR}

SELECT 

FROM ${tableName}
WHERE IS_DELETED = 0 AND ID in 

 #{item}

 SELECT 

FROM ${tableName}
WHERE IS_DELETED = 0 AND BUSINESS_ID = #{businessId,jdbcType=VARCHAR}

	AND TYPE_ID = #{typeId,jdbcType=VARCHAR}

SELECT 

FROM ${tableName}
WHERE IS_DELETED = 0 
AND ID = #{id}

SELECT 

FROM T_SES_EMS_EXPERT_ATTACHMENT
WHERE IS_DELETED = 0 
AND type_id = #{typeId}
AND business_id = #{businessId}

SELECT 

FROM ${tableName}
WHERE IS_DELETED = 0 
AND BUSINESS_ID = #{businessId}

SELECT 

FROM ${tableName}
WHERE IS_DELETED = 0 
AND to_char(CREATED_AT,'yyyy-MM-dd') = #{date}

SELECT count(1)
from ${tableName}
WHERE IS_DELETED = 0 
AND ID = #{id}

mapper.update:
update  ${tableName} 

  
    NAME = #{name},
  
  
    PATH = #{path},
  
   
    BUSINESS_ID = #{businessId},
  
   
    TYPE_ID = #{typeId},
  
  
    FILE_SIZE = #{size,jdbcType=BIGINT},
  
  
    CREATED_AT = #{createDate,jdbcType=TIMESTAMP},
  
  
    UPDATED_AT = #{updateDate,jdbcType=TIMESTAMP},
  
  
    IS_DELETED = #{isDelete,jdbcType=INTEGER}
  

WHERE ID = #{id,jdbcType=VARCHAR}
