mapper.select:
SELECT

FROM T_SES_EMS_PROJECT_EXTRACT_INFO
WHERE IS_DELETED = 0 AND STATUS IN (1,2)

    AND PROJECT_NAME like CONCAT(CONCAT('%', #{projectName}), '%')


    AND CODE like CONCAT(CONCAT('%', #{code}), '%')


    AND PURCHASE_WAY = #{purchaseWay}


    AND to_char(CREATED_AT,'yyyy-MM-dd hh24:mi:ss') >= #{startTime,jdbcType=VARCHAR}


    AND to_char(CREATED_AT,'yyyy-MM-dd hh24:mi:ss') <= #{endTime,jdbcType=VARCHAR} 


    AND PROCUREMENT_DEP_ID = #{procurementDepId,jdbcType=VARCHAR}


    AND IS_AUTO = #{isAuto}

ORDER BY CREATED_AT DESC

SELECT * FROM T_SES_EMS_PROJECT_EXTRACT_INFO WHERE 
to_char(UPDATED_AT,'yyyy-MM-dd hh24:mi:ss')  >= #{start,jdbcType=VARCHAR}
and to_char(UPDATED_AT,'yyyy-MM-dd hh24:mi:ss') <=#{end,jdbcType=VARCHAR}
