mapper.select:

select 

from T_SES_SMS_SUPPLIER_EXTRACTS E LEFT JOIN T_SES_BMS_DICTIONARY_DATA D ON E.PURCHASE_TYPE = D.ID
where E.ID = #{id,jdbcType=VARCHAR}

SELECT
FROM T_SES_SMS_SUPPLIER_EXTRACTS WHERE 1=1
AND PROJECT_NAME like CONCAT(CONCAT('%', #{projectName}), '%')
AND to_date(to_char(EXTRACTION_TIME,'yyyy-MM-dd'),'yyyy-MM-dd') = to_date(to_char(#{extractionTime,jdbcType=TIMESTAMP},'yyyy-MM-dd'),'yyyy-MM-dd')
AND PROJECT_ID=#{projectId,jdbcType=VARCHAR}
AND ID = #{id,jdbcType=VARCHAR}
