mapper.select:
SELECT ,U.REL_NAME EXTRACT_USER ,D.NAME PURCHASE_TYPE,D1.NAME PROJECT_TYPE from T_SES_SMS_EXTRACT_RECORD E LEFT JOIN T_SES_BMS_USER U ON E.EXTRACT_USER = U.ID LEFT JOIN T_SES_BMS_DICTIONARY_DATA D ON E.PURCHASE_TYPE = D.ID  
LEFT JOIN T_SES_BMS_DICTIONARY_DATA D1 ON E.PROJECT_TYPE = D1.CODE WHERE  E.IS_DELETE = '0' AND PROJECT_INTO IS NULL

	AND  E.EXTRACT_THE_WAY = #{extractTheWay}


	AND E.PROCUREMENT_DEP_ID = #{procurementDepId}  


    AND E.PROJECT_NAME like CONCAT(CONCAT('%', #{projectName}), '%')


    AND E.PROJECT_CODE like CONCAT(CONCAT('%', #{projectCode}), '%')


    AND E.PURCHASE_TYPE = #{purchaseType}


    AND E.CREATED_AT >= to_date(#{startTime,jdbcType=VARCHAR},'yyyy-MM-dd hh24:mi:ss')


    AND E.CREATED_AT  <= to_date(#{endTime,jdbcType=VARCHAR},'yyyy-MM-dd hh24:mi:ss')

 ORDER BY E.CREATED_AT DESC

SELECT   from T_SES_SMS_EXTRACT_RECORD E  WHERE E.IS_DELETE = '0' 

	AND E.EXTRACT_THE_WAY = #{extractTheWay}  


	AND E.PROCUREMENT_DEP_ID = #{procurementDepId}  


    AND E.PROJECT_NAME like CONCAT(CONCAT('%', #{projectName}), '%')


    AND E.STATUS IN (#{status})


    AND E.PROJECT_CODE like CONCAT(CONCAT('%', #{projectCode}), '%')


    AND E.PURCHASE_TYPE = #{purchaseType}


    AND E.UPDATED_AT >= to_date(#{startTime,jdbcType=VARCHAR},'yyyy-MM-dd hh24:mi:ss')


    AND E.UPDATED_AT  <= to_date(#{endTime,jdbcType=VARCHAR},'yyyy-MM-dd hh24:mi:ss')

 ORDER BY E.UPDATED_AT DESC
