mapper.select:
select
,
from T_ISS_PS_ARTICLE where ID = #{id}

	select

	,

  from T_ISS_PS_ARTICLE
  where IS_DELETED = 0

  	and USER_ID = #{userId}


      AND NAME LIKE #{name}


       AND ARTICLE_TYPE_ID = #{articleType.id}


      AND RANGE = #{range}


      AND STATUS = #{status}

  order by decode(STATUS,0,0,3,1,4,2,1,3,2,4), CREATED_AT DESC

select
,
from T_ISS_PS_ARTICLE where ID = #{id}

  	select

  	,

    from T_ISS_PS_ARTICLE
    where IS_DELETED = 0 AND STATUS in (1,2)

	  	  AND NAME LIKE #{name}


       	 AND ARTICLE_TYPE_ID = #{articleType.id}


	  	  AND RANGE = #{range}


        AND STATUS = #{status}


    order by STATUS ASC, PUBLISHED_AT DESC , SUBMIT_AT DESC

  	select

  	,

    from T_ISS_PS_ARTICLE
    where IS_DELETED = 0

	  	  AND NAME LIKE #{name}


       	 AND ARTICLE_TYPE_ID = #{articleType.id}


	  	  AND RANGE = #{range}


	  	  AND STATUS = #{status}


    order by CREATED_AT DESC

select
,
from T_ISS_PS_ARTICLE where IS_DELETED = 0 AND ID != #{id}

mapper.update:
update T_ISS_PS_ARTICLE
set IS_DELETED = 1
where ID = #{id}

mapper.select:
  	select

  	,

    from T_ISS_PS_ARTICLE a

    where IS_DELETED = 0
    	AND PROJECT_ID = #{projectId}

	  	  AND a.STATUS = #{status}


	  	  AND a.ARTICLE_TYPE_ID = #{articleType.id}

