#{item}
and status=2 and IS_DELETED = 0 order by published_at desc

SELECT * FROM T_ISS_PS_ARTICLE WHERE LAST_ARTICLE_TYPE_ID = #{id} or LAST_ARTICLE_TYPE_ID = #{twoid}
and name like '%'||#{title}||'%'
and status=2 and IS_DELETED = 0 order by published_at desc

select * from ( SELECT * FROM T_ISS_PS_ARTICLE WHERE LAST_ARTICLE_TYPE_ID in (SELECT ID FROM T_ISS_PS_ARTICLE_TYPE WHERE PARENT_ID in
#{item}
) and IS_DELETED = 0 AND status = 2 ORDER BY PUBLISHED_AT DESC ) where 1=1 and ROWNUM <=10

select * from ( SELECT * FROM T_ISS_PS_ARTICLE WHERE LAST_ARTICLE_TYPE_ID in
#{item}
and IS_DELETED = 0 AND status = 2 ORDER BY PUBLISHED_AT DESC ) where 1=1 and ROWNUM <=10

select * from ( SELECT * FROM T_ISS_PS_ARTICLE WHERE LAST_ARTICLE_TYPE_ID in (SELECT ID FROM T_ISS_PS_ARTICLE_TYPE WHERE PARENT_ID in
#{item}
)
and name like '%'||#{title}||'%'
and IS_DELETED = 0 AND status = 2 ORDER BY PUBLISHED_AT DESC ) where 1=1

select
,
FROM T_ISS_PS_ARTICLE WHERE IS_DELETED =0 AND STATUS > 1 AND RANGE in(1,2) AND to_char(PUBLISHED_AT,'yyyy-MM-dd hh24:mi:ss') >= #{startTime} AND to_char(PUBLISHED_AT,'yyyy-MM-dd hh24:mi:ss') <= #{endTime}
