,
from T_ISS_PS_ARTICLE where STATUS=2 and is_deleted=0 and ID = #{id}

	select
	
	,
  
  from T_ISS_PS_ARTICLE
  where IS_DELETED = 0
  
  	and USER_ID = #{userId}
  
  
      AND NAME LIKE CONCAT(CONCAT('%', #{name}), '%')
    
    
       AND ARTICLE_TYPE_ID = #{articleType.id}
      
    
      AND RANGE = #{range}
    
    
      AND STATUS = #{status}
    
  
   order by CREATED_AT DESC

select
,
from T_ISS_PS_ARTICLE where ID = #{id}

  	select
  	
