select
,
from T_ISS_PS_ARTICLE where IS_DELETED = 0 AND ID != #{id}

  	select
  	
  	,
    
    from T_ISS_PS_ARTICLE a
  
    where IS_DELETED = 0
    	AND PROJECT_ID = #{projectId}
	  	
	  	  AND a.STATUS = #{status}
	  	
	  	
	  	  AND a.ARTICLE_TYPE_ID = #{articleType.id}

SELECT * FROM ( SELECT * FROM T_ISS_PS_ARTICLE WHERE LAST_ARTICLE_TYPE_ID IN ( SELECT ID FROM T_ISS_PS_ARTICLE_TYPE WHERE PARENT_ID IN
#{item}
) ORDER BY published_at DESC ) WHERE 1 = 1 AND status = 2 AND IS_DELETED = 0 AND ROWNUM <= 6

SELECT c.*,a.category_id,b.name as categoryName,d.name as lastArticleTypeName FROM T_ISS_PS_ARTICLE c left join T_SES_BMS_ARTICLE_CATEGORY a on c.id=a.article_id left join T_SES_BMS_CATEGORY b on a.category_id=b.id left join T_ISS_PS_ARTICLE_TYPE d on c.LAST_ARTICLE_TYPE_ID=d.id WHERE 1=1 and d.PARENT_ID = #{parId}
and c.name like CONCAT(CONCAT('%', #{title}), '%')
and a.category_id = CONCAT(CONCAT('%', #{productType}), '%')
and d.name like CONCAT(CONCAT('%', #{lastArticleTypeName}), '%')
AND to_char(PUBLISHED_AT,'yyyy-mm-dd') >= #{publishStartDate}
AND to_char(PUBLISHED_AT,'yyyy-mm-dd') <= #{publishEndDate}
and c.status=2 and c.IS_DELETED = 0 order by published_at desc

select * from (SELECT * FROM T_ISS_PS_ARTICLE WHERE LAST_ARTICLE_TYPE_ID in
#{item}
order by PUBLISHED_AT desc ) where 1=1 and status = 2 and IS_DELETED = 0 and rownum <=6

SELECT c.*,a.category_id,b.name as categoryName,d.name as lastArticleTypeName FROM T_ISS_PS_ARTICLE c left join T_SES_BMS_ARTICLE_CATEGORY a on c.id=a.article_id left join T_SES_BMS_CATEGORY b on a.category_id=b.id left join T_ISS_PS_ARTICLE_TYPE d on c.LAST_ARTICLE_TYPE_ID=d.id WHERE LAST_ARTICLE_TYPE_ID IN ( SELECT ID FROM T_ISS_PS_ARTICLE_TYPE WHERE PARENT_ID = #{id} or parent_id = #{twoid} )
and c.name like CONCAT(CONCAT('%', #{title}), '%')
and a.category_id = #{productType}
and d.name like CONCAT(CONCAT('%', #{lastArticleTypeName}), '%')
AND to_char(PUBLISHED_AT,'yyyy-mm-dd') >= #{publishStartDate}
AND to_char(PUBLISHED_AT,'yyyy-mm-dd') <= #{publishEndDate}
and c.status=2 and c.IS_DELETED = 0 order by published_at desc

SELECT
	COUNT (ID)
FROM
	t_iss_ps_article
WHERE
	to_char(PUBLISHED_AT,'yyyy-mm-dd') >= #{firstday}
	AND to_char(PUBLISHED_AT,'yyyy-mm-dd') <= #{lastday}
AND ARTICLE_TYPE_ID = #{id}
and IS_DELETED = 0
and status = 2

SELECT c.*,a.category_id,b.name as categoryName,d.name as lastArticleTypeName FROM T_ISS_PS_ARTICLE c left join T_SES_BMS_ARTICLE_CATEGORY a on c.id=a.article_id left join T_SES_BMS_CATEGORY b on a.category_id=b.id left join T_ISS_PS_ARTICLE_TYPE d on c.LAST_ARTICLE_TYPE_ID=d.id WHERE 1=1 AND ARTICLE_TYPE_ID = #{articleTypeId} AND SECOND_ARTICLE_TYPE_ID = #{secondArticleTypeId}
and c.name like CONCAT(CONCAT('%', #{title}), '%')
and a.category_id = #{productType}
and d.name like CONCAT(CONCAT('%', #{lastArticleTypeName}), '%')
AND to_char(PUBLISHED_AT,'yyyy-mm-dd') >= #{publishStartDate}
AND to_char(PUBLISHED_AT,'yyyy-mm-dd') <= #{publishEndDate}
and c.status=2 and c.IS_DELETED = 0 order by published_at desc

SELECT
,
FROM T_ISS_PS_ARTICLE WHERE IS_DELETED =0 AND STATUS > 1 AND RANGE = 2 AND to_char(PUBLISHED_AT,'yyyy-MM-dd hh24:mi:ss') >= #{startTime} AND to_char(PUBLISHED_AT,'yyyy-MM-dd hh24:mi:ss') <= #{endTime}

SELECT
,
FROM T_ISS_PS_ARTICLE WHERE IS_DELETED =0 AND STATUS > 1 AND RANGE = 2 AND to_char(CANCAL_PUBLISH_AT,'yyyy-MM-dd hh24:mi:ss') >= #{startTime} AND to_char(CANCAL_PUBLISH_AT,'yyyy-MM-dd hh24:mi:ss') <= #{endTime}

select
*
from T_SES_BMS_ARTICLE_CATEGORY  
  
    "CATEGORY_ID" = #{categoryId}  
    and "ARTICLE_ID" = #{articleId}

SELECT
FROM T_SES_BMS_ARTICLE_CATEGORY A LEFT JOIN T_ISS_PS_ARTICLE B ON A.CATEGORY_ID=B.ID WHERE IS_DELETED = 0 AND STATUS = 2 AND ARTICLE_TYPE_ID = #{articleTypeId} AND SECOND_ARTICLE_TYPE_ID = #{secondArticleTypeId}
and name like CONCAT(CONCAT('%', #{title}), '%')
