mapper.select:
select

from T_SES_BMS_CATEGORY
where ID = #{id}

select

from T_SES_BMS_CATEGORY
where  IS_DELETED=0 and  CODE=#{code}

select

from T_SES_BMS_CATEGORY
where  IS_DELETED=0 and  NAME=#{name} and PARENT_ID=#{pId}

select

from T_SES_BMS_CATEGORY
where IS_DELETED=0 and ORGNIZATION_ID =#{orgnization.id}

select * from T_SES_BMS_CATEGORY where IS_DELETED=0

	and KIND like #{kind}


	and PARENT_ID = #{parentId}

mapper.update:
UPDATE T_SES_BMS_CATEGORY SET
IS_DELETED = 1 WHERE

   OR ID=#{tree.id}

mapper.select:
SELECT

FROM T_SES_BMS_CATEGORY
WHERE IS_DELETED=0 
AND PARENT_ID = #{id} 
AND PARAM_STATUS >= #{status}
ORDER BY POSITION

    SELECT
	
	FROM T_SES_BMS_CATEGORY
	
		1=1
		
			and	IS_DELETED = #{isDeleted}
		
	    
			and IS_PUBLISH = #{isPublish}
		
		 
			and CLASSIFY_STATUS in (1,3)
		
		 
			and CLASSIFY_STATUS in (2,3)
		
		 
			and PARAM_STATUS =#{paramStatus}
		
		
			and PARENT_ID= #{parentId}

SELECT

FROM T_SES_BMS_CATEGORY
WHERE ID = #{_parameter}

SELECT

FROM T_SES_BMS_CATEGORY
WHERE PARENT_ID = #{_parameter}

select

from T_SES_BMS_CATEGORY
where IS_DELETED=0 and NAME like '%'||#{name}||'%'

	and IS_PUBLISH = #{isPublish}


	and PARENT_ID= #{parentId}

	select id 
    	from T_SES_BMS_CATEGORY
		where 	 
			  NAME =#{name}
		 
			AND CODE =#{code}

	 select 
	 
	  from T_SES_BMS_CATEGORY where 
    to_char(UPDATED_AT,'yyyy-MM-dd hh24:mi:ss')  >= #{start,jdbcType=VARCHAR}
    and to_char(UPDATED_AT,'yyyy-MM-dd hh24:mi:ss') <=#{end,jdbcType=VARCHAR}

	 select 
	 
	 from T_SES_BMS_CATEGORY where 
     to_char(CREATED_AT,'yyyy-MM-dd hh24:mi:ss')  >= #{start,jdbcType=VARCHAR}
     and to_char(CREATED_AT,'yyyy-MM-dd hh24:mi:ss') <=#{end,jdbcType=VARCHAR}

select count(*)  
from T_SES_BMS_CATEGORY
where ID = #{id}
