mapper.select:
SELECT

FROM T_SES_BMS_CATEGORY
WHERE IS_DELETED=0 
AND PARENT_ID = #{id} 
AND PARAM_STATUS >= #{status}
ORDER BY POSITION

    SELECT
	
	FROM T_SES_BMS_CATEGORY
	
		1=1
		
			and	IS_DELETED = #{isDeleted}
		
	    
			and IS_PUBLISH = #{isPublish}
		
		 
			and CLASSIFY_STATUS in (1,3)
		
		 
			and CLASSIFY_STATUS in (2,3)
		
		 
			and PARAM_STATUS =#{paramStatus}
		
		
			and PARENT_ID= #{parentId}

SELECT

FROM T_SES_BMS_CATEGORY
WHERE ID = #{_parameter}

SELECT

FROM T_SES_BMS_CATEGORY
WHERE PARENT_ID = #{_parameter}

select

from T_SES_BMS_CATEGORY
where IS_DELETED=0 and NAME like CONCAT(CONCAT('%', #{name}), '%')

	and IS_PUBLISH = #{isPublish}


	and PARENT_ID= #{parentId}

	select id 
    	from T_SES_BMS_CATEGORY
		where 	 
			  NAME =#{name}
		 
			AND CODE =#{code}

	 select 
	 
	  from T_SES_BMS_CATEGORY where 
    to_char(UPDATED_AT,'yyyy-MM-dd hh24:mi:ss')  >= #{start,jdbcType=VARCHAR}
    and to_char(UPDATED_AT,'yyyy-MM-dd hh24:mi:ss') <=#{end,jdbcType=VARCHAR}

	 select 
	 
	 from T_SES_BMS_CATEGORY where 
     to_char(CREATED_AT,'yyyy-MM-dd hh24:mi:ss')  >= #{start,jdbcType=VARCHAR}
     and to_char(CREATED_AT,'yyyy-MM-dd hh24:mi:ss') <=#{end,jdbcType=VARCHAR}

select count(*)  
from T_SES_BMS_CATEGORY
where ID = #{id}

select * from T_SES_BMS_CATEGORY 
start with ID = #{id} 
connect by prior PARENT_ID = ID

SELECT T.NAME FROM T_SES_BMS_CATEGORY T WHERE IS_PUBLISH = 0 AND IS_DELETED = 0
AND T.ID IN
#{category}

SELECT
	t1.*
FROM
	 T_SES_BMS_CATEGORY T1
LEFT JOIN T_SES_EMS_EXPERT_CATEGORY T2 ON T1.ID = T2.CATEGORY_ID
WHERE
	T2.EXPERT_ID = #{expertId}

	AND T2.LEVELS = #{levels}


	AND T2.AUDIT_STATUS = #{auditStatus}

SELECT DISTINCT ID, NAME, CODE, PARENT_ID, IS_PARENT FROM( SELECT ID, NAME, CODE, PARENT_ID, IS_PARENT FROM T_SES_BMS_CATEGORY WHERE 1 = 1 AND IS_PUBLISH = 0 AND IS_DELETED = 0 ) START WITH (ID IN ( SELECT ID FROM T_SES_BMS_CATEGORY WHERE 1 = 1 AND IS_PUBLISH = 0 AND IS_DELETED = 0
AND CLASSIFY_TYPE = #{type}
AND NAME LIKE CONCAT(CONCAT('%', #{cateName}), '%')
AND CODE = #{codeName}
) ) CONNECT BY PRIOR PARENT_ID = ID ORDER BY CODE

SELECT ID,NAME,PARENT_ID
FROM T_SES_BMS_CATEGORY
WHERE ID = #{id}
