mapper.update:
 
		UPDATE T_SES_BMS_CATEGORY_PARAMETER 
		SET UPDATED_AT = #{updatedAt}
		
			,PARAM_NAME = #{paramName}
		
		
			,PARAM_TYPE_ID = #{paramTypeId}
		
		
			,IS_DELETED = #{isDeleted}
		
		WHERE ID = #{id}

mapper.select:
SELECT COUNT(1)
FROM T_SES_BMS_CATEGORY_PARAMETER
WHERE CATEGORY_ID = #{cateId,jdbcType=VARCHAR}
