mapper.sql:
where dd.IS_DELETED = 0 

  
    and dd.ID = #{id} 
  
  
    and dd.CODE = #{code} 
  
  
    and dd.NAME  like CONCAT(CONCAT('%', #{name}), '%') 
  
  
    and dd.DESCRIPTION  like CONCAT(CONCAT('%', #{description}), '%') 
  
  
    and dd.CREATED_AT = #{createdAt} 
  
  
    and dd.UPDATED_AT = #{updatedAt} 
  
  
    and dd.KIND = #{kind} 
  
  
    and dd.POSITION = #{position}

where dd.IS_DELETED = 0

  
    and dd.ID = #{id}
  
  
    and dd.CODE <> #{code}
  
  
    and dd.NAME  like CONCAT(CONCAT('%', #{name}), '%')
  
  
    and dd.DESCRIPTION  like CONCAT(CONCAT('%', #{description}), '%')
  
  
    and dd.CREATED_AT = #{createdAt}
  
  
    and dd.UPDATED_AT = #{updatedAt}
  
  
    and dd.KIND = #{kind}
  
  
    and dd.POSITION = #{position}

