mapper.sql:
where IS_DELETED=0

  
    and ID = #{id}
  
  
    and NAME = #{name}
  
  
    and MENU_TYPE = #{type}
  
  
    and STATUS = #{status}
  
  
    and URL = #{url}
  
  
    and CREATED_AT = #{createdAt,jdbcType=TIMESTAMP}
  
  
    and POSITION = #{position}
  
  
    and MENU_LEVEL = #{menulevel}
  
  
    and PARENT_ID = #{parentId.id}
  
  
    and UPDATED_AT = #{updatedAt,jdbcType=TIMESTAMP}
  
  
    and KIND = #{kind}
  
  
    and ICON = #{icon}
