mapper.sql:
where IS_DELETED=0
AND STATUS = 0

  
    and ID = #{id}
  
  
    and NAME LIKE CONCAT(CONCAT('%', #{name}), '%')
  
  
    and CREATED_AT = #{createdAt}
  
  
    and UPDATED_AT = #{updatedAt}
  
  
    and DESCRIPTION like concat(concat('%', '#{description}'), '%')
  
  
    and CODE = #{code}
  
  
    and KIND = #{kind}
  
  
    and STATUS = #{status}
  
  
    and POSITION = #{position}

mapper.select:
select r. ID AS ID, r. NAME AS NAME, r.CREATED_AT AS CREATED_AT, r.DESCRIPTION AS DESCRIPTION, r.IS_DELETED AS IS_DELETED, r.UPDATED_AT AS UPDATED_AT, r.STATUS AS STATUS, r.CODE AS CODE, r.KIND AS KIND, r.POSITION AS POSITION, U .ID AS userid, U .LOGIN_NAME AS uloginName, U .PASSWORD AS upassword, U .CREATED_AT AS uCreatedAt, U .IS_DELETED AS uIsDeleted, U .REL_NAME AS urelName, U .UPDATED_AT AS uUpdatedAt, U .MOBILE AS umobile, U .GENDER AS ugender, U .TELEPHONE AS utelephone, U .DUTIES AS uduties, U .TYPE_NAME AS utypeName, U .TYPE_ID AS utypeId, U .ADDRESS AS uaddress, U .EMAIL AS uemail, U .ORG_ID AS org, U .USER_ID AS creater, U .RANDOM_CODE AS urandomCode, U .ORG_NAME AS uorgName, U .PUBLISH_TYPE AS upublishType, U .ID_NUMBER AS uidNumber, U .OFFICER_CERT_NO AS uofficerCertNo, U .NET_TYPE AS unetType, U .ERROR_NUM AS uerrorNum, U .DATA_ACCESS AS udataAccess, U .MOBILE2 AS umobile2, U .TELEPHONE2 AS utelephone2, pm. ID AS mid, pm. NAME AS mname, pm.MENU_TYPE AS mtype, pm.IS_DELETED AS misDeleted, pm.STATUS AS mstatus, pm.URL AS murl, pm.CREATED_AT AS mcreatedAt, pm.UPDATED_AT AS mupdatedAt, pm.POSITION AS mposition, pm.MENU_LEVEL AS menulevel, pm.PARENT_ID AS parentId, pm.KIND AS kind FROM T_SES_BMS_ROLE r LEFT JOIN T_SES_BMS_ROLE_PERMISSION rp ON rp.ROLE_ID = r. ID LEFT JOIN T_SES_BMS_PERMISSION pm ON pm. ID = rp.PERMENU_ID LEFT JOIN T_SES_BMS_USER_ROLE ur ON r. ID = ur.ROLE_ID LEFT JOIN T_SES_BMS_USER U ON ur.USER_ID = U . ID WHERE r.IS_DELETED = 0
and r.ID=#{id}
ORDER BY r.POSITION ASC

SELECT count(1) FROM T_SES_BMS_ROLE 
WHERE IS_DELETED = 0 
AND ID IN(
   SELECT ROLE_ID from T_SES_BMS_USER_ROLE 
   WHERE USER_ID = #{userId}
)
AND CODE = #{code}

SELECT r. ID AS ID, r. NAME AS NAME, r.CREATED_AT AS CREATED_AT, r.DESCRIPTION AS DESCRIPTION, r.IS_DELETED AS IS_DELETED, r.UPDATED_AT AS UPDATED_AT, r.STATUS AS STATUS, r.CODE AS CODE, r.KIND AS KIND, r.POSITION AS POSITION FROM T_SES_BMS_ROLE r LEFT JOIN T_SES_BMS_USER_ROLE ur ON r. ID = ur.ROLE_ID LEFT JOIN T_SES_BMS_USER u ON ur.USER_ID = u.ID WHERE r.IS_DELETED = 0 AND r.STATUS = 0
AND u.ID = #{userId}
AND r.code = #{code}

select 

from T_SES_BMS_ROLE
where 1=1

AND	POSITION >= #{position}


AND	POSITION > #{oldPosition}
AND	POSITION <= #{position}


AND	POSITION >= #{position}
AND	POSITION < #{oldPosition}

SELECT u.USER_ID from T_SES_BMS_USER_ROLE u LEFT JOIN 
T_SES_BMS_role r on u.ROLE_ID=r.id 
where r.name=#{roleName}

SELECT ID, NAME, ( SELECT "COUNT" (DISTINCT(ur.USER_ID)) FROM T_SES_BMS_USER U LEFT JOIN T_SES_BMS_USER_ROLE ur ON U . ID = ur.USER_ID WHERE U .IS_DELETED = 0 AND ur.ROLE_ID = r.ID ) AS userNumber, CREATED_AT, DESCRIPTION, STATUS, CODE, KIND, POSITION FROM T_SES_BMS_ROLE r WHERE IS_DELETED = 0
AND NAME LIKE CONCAT(CONCAT('%', #{name}), '%')
AND STATUS = #{status}
AND CODE NOT IN
#{item}
ORDER BY POSITION ASC
