mapper.sql:
    where u.IS_DELETED = 0 
    
      
        and u.ID = #{id} 
      
      
        and u.LOGIN_NAME like CONCAT(CONCAT('%', #{loginName}), '%')
      
      
        and u.PASSWORD = #{password} 
      
      
        and u.CREATED_AT = #{createdAt} 
      
      
        and u.REL_NAME like CONCAT(CONCAT('%', #{relName}), '%')
      
      
        and u.ORG_NAME like CONCAT(CONCAT('%', #{orgName}), '%')
      
      
        and u.UPDATED_AT = #{updatedAt} 
      
      
        and u.USER_ID = #{user.id}
       
      
        and u.MOBILE = #{mobile}
      
      
        and u.GENDER = #{gender}
      
      
        and u.TELEPHONE = #{telephone}
      
      
        and u.EMAIL= #{email}
      
      
        and u.ORG_ID = #{org.id} 
      
      
        and u.DUTIES = #{duties} 
      
      
            
				and "INSTR"(roleId, #{roleId})>0
			
      
      
        and u.TYPE_ID = #{typeId}
      
      
        and u.ADDRESS = #{address}
      
      
        and u.NET_TYPE = #{netType}

mapper.select:
select

from T_SES_BMS_USER u
where u.IS_DELETED=0 
and u.MOBILE = #{mobile}

and u.ID != #{id}

select

from T_SES_BMS_USER u
where u.IS_DELETED=0 
and u.ID_NUMBER = #{idNumber}

and u.ID != #{id}

SELECT DISTINCT(u.ID), u.ID AS ID, u.LOGIN_NAME AS LOGIN_NAME, u.PASSWORD AS PASSWORD, u.CREATED_AT AS CREATED_AT, u.IS_DELETED AS IS_DELETED, u.REL_NAME AS REL_NAME, u.UPDATED_AT AS UPDATED_AT, u.USER_ID AS USER_ID, u.MOBILE AS MOBILE, u.GENDER AS GENDER, u.TELEPHONE AS TELEPHONE, u.EMAIL AS EMAIL, u.ORG_ID AS ORG_ID, u.DUTIES AS DUTIES, u.TYPE_NAME AS TYPE_NAME, u.TYPE_ID AS TYPE_ID, u.ADDRESS AS ADDRESS, u.RANDOM_CODE AS RANDOM_CODE, u.ORG_NAME AS ORG_NAME, u.PUBLISH_TYPE AS PUBLISH_TYPE, u.ID_NUMBER AS ID_NUMBER, u.OFFICER_CERT_NO AS OFFICER_CERT_NO, u.NET_TYPE AS NET_TYPE FROM T_SES_BMS_USER u LEFT JOIN T_SES_BMS_USER_ROLE ur ON u. ID = ur.USER_ID LEFT JOIN T_SES_BMS_ROLE r ON ur.ROLE_ID = r . ID WHERE u.IS_DELETED = 0
AND r.CODE = #{code}
AND r.ID = #{id}
