mapper.update:
update T_SES_EMS_EXAM_USER_SCORE


    IS_MAX = #{isMax},


where USER_ID = #{userId}
AND PAPER_ID = #{paperId}

update T_SES_EMS_EXAM_USER_SCORE


    IS_MAX = #{isMax},


where USER_ID = #{userId}
AND YEAR = #{year}

mapper.select:
SELECT U.ID AS userId, U.REL_NAME AS relName, s.USER_DUTY AS userDuty, s.TEST_DATE AS testDate, s.SCORE AS score, s.STATUS AS status, E.ID_TYPE AS idType, E.ID_NUMBER AS idNumber FROM T_SES_EMS_EXAM_USER_SCORE s LEFT JOIN T_SES_BMS_USER U ON s.USER_ID = U.ID LEFT JOIN T_SES_EMS_EXPERT E ON U.type_id = E.ID WHERE U.TYPE_NAME = 5 AND s.USER_TYPE = 1 AND s.IS_MAX=1
AND U.REL_NAME LIKE #{relName}
AND s.STATUS = #{status}
AND s.USER_DUTY = #{userDuty}
AND s.YEAR = #{year}
ORDER BY testDate nulls last

SELECT s.USER_ID AS userId, s.score AS score, d.CODE AS code, d.CARD as card, s.STATUS as status, s.TEST_DATE AS testDate, U.REL_NAME AS relName FROM T_SES_EMS_EXAM_USER_SCORE s, T_SES_BMS_USER U, T_SES_EMS_EXAM_PAPER_USER D WHERE s.PAPER_ID = d.PAPER_ID AND s.USER_ID = U.ID AND d.USER_ID = U.ID AND s.IS_MAX = 1
AND U.REL_NAME LIKE #{relName}
AND s.STATUS = #{status}
AND D.CODE LIKE #{code}
ORDER BY testDate nulls last

SELECT
FROM T_SES_EMS_EXAM_USER_SCORE WHERE
USER_ID = #{userId}
AND PAPER_ID = #{paperId}
ORDER BY CREATED_AT DESC

SELECT s.ID as id, p.CODE as code, s.SCORE as score, s.STATUS as status, s.TEST_DATE as testDate FROM T_SES_EMS_EXAM_USER_SCORE s, T_SES_EMS_EXAM_PAPER P WHERE s.PAPER_ID = P.ID AND s.USER_ID = #{userId}
AND P.CODE LIKE #{code}
ORDER BY s.CREATED_AT DESC
