mapper.select:
select
from T_SES_EMS_EXPERT_BLACKLIST where 1=1
AND REL_NAME like '%'|| #{relName} ||'%'
AND PUNISH_TYPE = #{punishType}
AND PUNISH_DATE = #{punishDate}
ORDER BY CREATED_AT DESC

select
from T_SES_EMS_EXPERT_BLACKLIST where STATUS <> 2
AND REL_NAME like '%'|| #{relName} ||'%'
AND PUNISH_TYPE = #{punishType}
AND PUNISH_DATE = #{punishDate}
ORDER BY CREATED_AT DESC

mapper.update:
update T_SES_EMS_EXPERT_BLACKLIST
set STATUS = #{status}
where ID = #{id}

mapper.select:
SELECT eb.*, CASE dd.KIND WHEN 6 THEN CONCAT(dd.NAME, '技术') ELSE dd.NAME END AS EXPERT_TYPE_NAME FROM T_SES_EMS_EXPERT_BLACKLIST eb LEFT JOIN T_SES_EMS_EXPERT e ON eb.EXPERT_ID = e.ID LEFT JOIN T_SES_BMS_DICTIONARY_DATA dd ON dd.ID = e.EXPERTS_TYPE_ID
AND eb.REL_NAME like #{expertBlacklist.relName}
AND e.EXPERTS_TYPE_ID = #{expertTypeId}
ORDER BY eb.DATE_OF_PUNISHMENT desc
