mapper.select:
select
from T_SES_EMS_EXPERT where 1=1 and IS_SUBMIT != '0' and IS_DELETE != 1 and (STATUS = '1' or STATUS = '4' or STATUS = '5')
AND REAL_NAME like '%${relName}%'
AND EXPERTS_FROM = '${expertsFrom}'
AND EXPERTS_TYPE_ID = ${expertsTypeId}
ORDER BY CREATED_AT DESC


select
count(*)
from T_SES_EMS_EXPERT
where 1=1 and IS_SUBMIT != '0' and
IS_DELETE != 1

	and REAL_NAME= #{relName,jdbcType=VARCHAR}


	and EXPERTS_TYPE_ID= #{expertsTypeId,jdbcType=VARCHAR}


	and STATUS = #{status,jdbcType=DECIMAL}

SELECT * FROM T_SES_EMS_EXPERT ee LEFT JOIN T_SES_EMS_EXPERT_CATEGORY ec ON ee."ID" = EC.EXPERT_ID WHERE ee.IS_DELETE = 0 AND ee.STATUS = 1 AND ee.IS_BLACK = 0
AND (EE.EXPERTS_TYPE_ID = #{item.expertsTypeId}
AND
EC.CATEGORY_ID = #{ids}
AND
EC.CATEGORY_ID=#{ids}
)

select

from T_SES_EMS_EXPERT
where 1=1 and IS_SUBMIT != '0' and IS_DELETE != 1

	AND REAL_NAME LIKE #{relName}


	AND EXPERTS_FROM = #{expertsFrom}


	AND STATUS = #{status}


	AND EXPERTS_TYPE_ID = #{expertsTypeId}


	AND ID_NUMBER LIKE #{idNumber}


	AND ID = #{id}

ORDER BY CREATED_AT DESC

