mapper.select:
select T1.ID, T1.REAL_NAME, T1.ID_CARD_NUMBER, T1.GENDER, T1.EXPERTS_TYPE_ID, T1.GRADUATE_SCHOOL, T1.STATUS, T1.IS_PROVISIONAL, T1.EXPERTS_FROM, T1.MOBILE, T1.IS_SUBMIT, T1.CREATED_AT, T1.SUBMIT_AT, T1.AUDIT_AT, T1.COVER_NOTE, T1.AUDIT_TEMPORARY, T2.LOGIN_NAME AS loginName, org.SHORT_NAME AS orgName from T_SES_EMS_EXPERT t1 LEFT JOIN T_SES_BMS_USER t2 ON T1.ID = T2.TYPE_ID LEFT JOIN T_SES_OMS_PURCHASE_DEP dep ON T1.PURCHASE_DEP_ID = dep.ID LEFT JOIN T_SES_OMS_ORG org ON org.ID = dep.ORG_ID where 1=1 and IS_DELETE = 0
AND T1.REAL_NAME like CONCAT(CONCAT('%', #{relName}), '%')
AND T1.MOBILE like CONCAT(CONCAT('%', #{mobile}), '%')
AND T1.EXPERTS_FROM = #{expertsFrom}
AND IS_PROVISIONAL = '0'
AND T1.STATUS = #{status}
AND IS_PROVISIONAL = '1' AND T1.STATUS = '4'
AND instr(T1.EXPERTS_TYPE_ID, #{expertsTypeId} )>0
AND T1.IS_PUBLISH = #{isPublish}
and ID_CARD_NUMBER like CONCAT(CONCAT('%', #{idCardNumber}), '%')
and GRADUATE_SCHOOL like CONCAT(CONCAT('%', #{graduateSchool}), '%')
and org.SHORT_NAME =#{orgName}
and T1.AUDIT_TEMPORARY = #{auditTemporary}
ORDER BY T1.SUBMIT_AT DESC NULLS LAST

select
from T_SES_EMS_EXPERT where 1=1 and IS_SUBMIT != '0' and IS_DELETE != 1 and (STATUS = '1' or STATUS = '4' or STATUS = '5')
AND REAL_NAME like CONCAT(CONCAT('%', #{relName}), '%')
AND EXPERTS_FROM = '${expertsFrom}'
AND EXPERTS_TYPE_ID = #{expertsTypeId}
ORDER BY CREATED_AT DESC


select
count(*)
from T_SES_EMS_EXPERT
where 1=1 and IS_SUBMIT != '0' and
IS_DELETE != 1

    and REAL_NAME= #{relName,jdbcType=VARCHAR}


    and EXPERTS_TYPE_ID= #{expertsTypeId,jdbcType=VARCHAR}


    and STATUS = #{status}

select

from T_SES_EMS_EXPERT
where ID = #{id,jdbcType=VARCHAR}

        SELECT * FROM T_SES_EMS_EXPERT t1 WHERE T1.id IN (
        SELECT
        EE."ID"
        FROM
        T_SES_EMS_EXPERT ee
        LEFT JOIN T_SES_EMS_EXPERT_CATEGORY ec
        ON ee."ID" =
        EC.EXPERT_ID
        WHERE 1=1 AND
        ee.IS_DELETE = 0
        AND (ee.STATUS = 4 OR
        ee.STATUS = 7)
        AND
        ee.IS_BLACK = 0
        AND ee.IS_SUBMIT = '1'
        AND NVL(IS_PROVISIONAL,0) = 0

        
            AND
            
                EE.EXPERTS_FROM = #{fromsplit}
            
        
        
            AND ee.ADDRESS = #{addressId}
        
        
            AND
            
                ee.ADDRESS = #{split}
            
        
        
            AND (
            (
            TO_CHAR (EE.BIRTHDAY, 'yyyy') >= #{ageMin}
            AND TO_CHAR
            (EE.BIRTHDAY, 'yyyy') <= #{ageMax}
            )
            OR (
            TO_CHAR (EE.BIRTHDAY,
            'yyyy') <= #{ageMin}
            AND TO_CHAR (EE.BIRTHDAY, 'yyyy') >=
            #{ageMax}
            )
            )
        
        
            
                AND (
                
                    "INSTR"(EE.EXPERTS_TYPE_ID,#{typeId})>0
                
            
            
                AND PROFESS_TECH_TITLES = #{item.expertsQualification}
            
            
                
                    AND
                    
                        EC.CATEGORY_ID = #{ids}
                    
                
                
                    AND
                    
                        EC.CATEGORY_ID=#{ids}
                    
                
            
            
                )
            
        

        GROUP BY EE."ID"
        )

select

from T_SES_EMS_EXPERT
where 1=1 and IS_SUBMIT != '0' and IS_DELETE != 1

    AND REAL_NAME LIKE CONCAT(CONCAT('%', #{relName}), '%')


    AND EXPERTS_FROM = #{expertsFrom}


    AND STATUS = #{status}


    AND EXPERTS_TYPE_ID = #{expertsTypeId}


    AND ID_NUMBER LIKE CONCAT(CONCAT('%', #{idNumber}), '%')


    AND ID = #{id}


    
        #{ids}
    

ORDER BY CREATED_AT DESC

SELECT ID FROM T_SES_EMS_EXPERT
WHERE IS_DELETE = 0 AND IS_PROVISIONAL=0 AND ID_CARD_NUMBER = #{param1}

    AND ID != #{param2}

SELECT ID FROM T_SES_EMS_EXPERT
