    AND ID != #{param2}

mapper.update:
update T_SES_EMS_EXPERT
set STEP_NUMBER = #{param2}
where id = #{param1}

mapper.select:
SELECT t1.*, t2.NAME as sex, org.SHORT_NAME AS orgName from T_SES_EMS_EXPERT t1 LEFT JOIN T_SES_BMS_DICTIONARY_DATA t2 ON t1.GENDER = t2.ID LEFT JOIN T_SES_OMS_PURCHASE_DEP dep ON t1.PURCHASE_DEP_ID = dep.ID LEFT JOIN T_SES_OMS_ORG org ON org.ID = dep.ORG_ID WHERE (
(IS_SUBMIT = '1' AND (t1.STATUS = '0' OR t1.STATUS = '1' OR t1.STATUS = '2' OR t1.STATUS = '9' OR t1.STATUS = '10' OR t1.STATUS = '15' OR t1.STATUS = '16' OR t1.STATUS = '5')) OR (t1.IS_SUBMIT = '0' AND t1.status = '3')
(t1.IS_SUBMIT = '1' AND (t1.STATUS = '6' OR t1.STATUS = '4' OR t1.STATUS = '5' OR t1.STATUS = '-2' OR t1.STATUS = '-3' OR t1.STATUS = '10'))
t1.PURCHASE_DEP_ID = #{purchaseDepId}
(t1.IS_SUBMIT = '1' AND (t1.STATUS = '6' OR t1.STATUS = '7' OR t1.STATUS = '8'))
1 = 1
)
AND t1.IS_PROVISIONAL = '0' AND t1.IS_DELETE = '0'
AND t1.PURCHASE_DEP_ID = #{purchaseDepId}
AND t1.EXTRACT_ORGID = #{extractOrgid}
AND t1.REAL_NAME LIKE CONCAT(CONCAT('%', #{relName}), '%')
AND org.SHORT_NAME LIKE CONCAT(CONCAT('%', #{orgName}), '%')
AND t1.EXPERTS_FROM = #{expertsFrom}
AND t1.EXPERTS_TYPE_ID LIKE CONCAT(CONCAT('%', #{expertsTypeId}), '%')
AND t1.STATUS in ('15','16')
AND t1.STATUS = #{status}
AND TO_CHAR(t1.UPDATED_AT,'YY-MM-DD') = TO_CHAR(#{updatedAt},'YY-MM-DD')
AND t1.AUDIT_TEMPORARY = #{auditTemporary}
AND TO_CHAR(t1.AUDIT_AT,'YY-MM-DD') = TO_CHAR(#{auditAt},'YY-MM-DD')
AND TO_CHAR(t1.AUDIT_AT,'YY-MM-DD') >= TO_CHAR(#{startTime},'YY-MM-DD')
AND TO_CHAR(t1.AUDIT_AT,'YY-MM-DD') <= TO_CHAR(#{endTime},'YY-MM-DD')
#{id}
ORDER BY t1.UPDATED_AT ASC NULLS LAST
ORDER BY t1.SUBMIT_AT ASC NULLS LAST

SELECT *
FROM T_SES_EMS_EXPERT
where 1=1

    and ID not in
    
        #{item}
    


    and IS_PROVISIONAL = #{expert.isProvisional,jdbcType=DECIMAL}


    and STATUS = #{expert.status}


    and IS_DELETE = #{expert.isDelete}


    and REAL_NAME LIKE CONCAT(CONCAT('%', #{expert.relName,jdbcType=VARCHAR}), '%')


    and MOBILE LIKE CONCAT(CONCAT('%', #{expert.mobile,jdbcType=VARCHAR}), '%')

ORDER BY CREATED_AT DESC

select * from T_SES_EMS_EXPERT where 1=1
#{id}
ORDER BY CREATED_AT DESC

mapper.update:
update
	T_SES_EMS_EXPERT
SET
	EXTRACT_ORGID =#{extractOrgid}
WHERE
	ID = #{id}

mapper.select:
select T1.ID, T1.REAL_NAME, T1.ID_CARD_NUMBER, T1.GENDER, T1.EXPERTS_TYPE_ID, T1.GRADUATE_SCHOOL, T1.STATUS, T1.IS_PROVISIONAL, T1.EXPERTS_FROM, T1.MOBILE, T1.IS_SUBMIT, T1.SUBMIT_AT, T1.AUDIT_AT, T2.LOGIN_NAME AS loginName, org.SHORT_NAME AS orgName, AREA.NAME AS ADDRESS from T_SES_EMS_EXPERT t1 LEFT JOIN T_SES_BMS_USER t2 ON T1.ID = T2.TYPE_ID LEFT JOIN T_SES_BMS_AREA A ON t1.address = A.ID LEFT JOIN T_SES_BMS_AREA AREA ON A.PARENT_ID = AREA.ID LEFT JOIN T_SES_OMS_PURCHASE_DEP dep ON T1.PURCHASE_DEP_ID = dep.ID LEFT JOIN T_SES_OMS_ORG org ON org.ID = dep.ORG_ID where (T1.STATUS = 6 or T1.STATUS = 7 or T1.STATUS = 13) AND T1.IS_PROVISIONAL = '0' AND T1.IS_DELETE = 0
AND T1.REAL_NAME like CONCAT(CONCAT('%', #{relName}), '%')
AND T1.MOBILE like CONCAT(CONCAT('%', #{mobile}), '%')
AND T1.EXPERTS_FROM = #{expertsFrom}
and ID_CARD_NUMBER like CONCAT(CONCAT('%', #{idCardNumber}), '%')
AND T1.STATUS = #{status}
AND T1.EXPERTS_FROM = #{expertsFrom}
AND instr(T1.EXPERTS_TYPE_ID, #{expertsTypeId})>0
AND org.SHORT_NAME like CONCAT(CONCAT('%', #{orgName}), '%')
AND org.ID = #{orgId}
AND A.PARENT_ID = #{address}
ORDER BY T1.CREATED_AT DESC

SELECT ID FROM T_SES_EMS_EXPERT
WHERE IS_DELETE = 0 AND IS_PROVISIONAL = 1

    AND ID_CARD_NUMBER = #{idCardNumber}


    AND MOBILE = #{mobile}


    AND ID <> #{id}

SELECT t1.ID, t1.REAL_NAME, t1.EXPERTS_FROM, t1.EXPERTS_TYPE_ID, t1.AUDIT_AT, t1.WORK_UNIT, t1.PROFESS_TECH_TITLES, t2.NAME as sex, org.SHORT_NAME AS orgName from T_SES_EMS_EXPERT t1 LEFT JOIN T_SES_BMS_DICTIONARY_DATA t2 ON t1.GENDER = t2.ID LEFT JOIN T_SES_OMS_PURCHASE_DEP dep ON t1.PURCHASE_DEP_ID = dep.ID LEFT JOIN T_SES_OMS_ORG org ON org.ID = dep.ORG_ID WHERE (
(IS_SUBMIT = '1' AND (t1.STATUS = '0' OR t1.STATUS = '1' OR t1.STATUS = '2' OR t1.STATUS = '9' OR t1.STATUS = '10' OR t1.STATUS = '15' OR t1.STATUS = '16' OR t1.STATUS = '5')) OR (t1.IS_SUBMIT = '0' AND t1.status = '3')
(t1.IS_SUBMIT = '1' AND (t1.STATUS = '6' OR t1.STATUS = '4' OR t1.STATUS = '5' OR t1.STATUS = '-2' OR t1.STATUS = '-3' OR t1.STATUS = '10'))
t1.PURCHASE_DEP_ID = #{purchaseDepId}
(t1.IS_SUBMIT = '1' AND (t1.STATUS = '6' OR t1.STATUS = '7' OR t1.STATUS = '8'))
1 = 1
) AND t1.IS_PROVISIONAL = '0' AND t1.IS_DELETE = '0'
AND t1.PURCHASE_DEP_ID = #{purchaseDepId}
AND t1.EXTRACT_ORGID = #{extractOrgid}
AND t1.REAL_NAME LIKE CONCAT(CONCAT('%', #{relName}), '%')
AND org.SHORT_NAME LIKE CONCAT(CONCAT('%', #{orgName}), '%')
AND t1.EXPERTS_FROM = #{expertsFrom}
AND t1.EXPERTS_TYPE_ID LIKE CONCAT(CONCAT('%', #{expertsTypeId}), '%')
AND t1.STATUS in ('15','16')
AND t1.STATUS = #{status}
AND TO_CHAR(t1.UPDATED_AT,'YY-MM-DD') = TO_CHAR(#{updatedAt},'YY-MM-DD')
AND t1.AUDIT_TEMPORARY = #{auditTemporary}
AND TO_CHAR(t1.AUDIT_AT,'YY-MM-DD') = TO_CHAR(#{auditAt},'YY-MM-DD')
AND TO_CHAR(t1.AUDIT_AT,'YY-MM-DD') >= TO_CHAR(#{startTime},'YY-MM-DD')
AND TO_CHAR(t1.AUDIT_AT,'YY-MM-DD') <= TO_CHAR(#{endTime},'YY-MM-DD')
#{id}
ORDER BY org.POSITION ASC,t1.AUDIT_AT ASC
