mapper.select:
SELECT pe.* FROM T_SES_EMS_PROJECT_EXTRACT pe Where STATUS_COUNT=0
AND pe.ID =#{id}
AND pe.EXPERT_CONDITION_ID =#{expertConditionId}
AND pe.OPERATING_TYPE in (${reason})
AND pe.PROJECT_ID =#{projectId} ORDER BY EXPERT_CONDITION_ID

mapper.delete:
delete from T_SES_EMS_PROJECT_EXTRACT
where EXPERT_ID in (select EXPERT_ID from T_SES_EMS_PROJECT_EXTRACT group by EXPERT_ID having count(EXPERT_ID) > 1)
and rowid not in (select min(rowid) from T_SES_EMS_PROJECT_EXTRACT group by EXPERT_ID having count(EXPERT_ID )>1) AND PROJECT_ID=#{projectId} AND EXPERT_CONDITION_ID=#{expertConditionId}
