mapper.update:
    	 update T_SES_OMS_ORG s
	     set 
	         s.STATUS = #{status}
	     where s.ID=#{id}

mapper.select:
select d.id as id, d.NAME as name, d.TYPE_NAME as typeName, d.ADDRESS as address, d.MOBILE as mobile, d.POST_CODE as postCode, d.ORG_CODE as orgCode, d.TELEPHONE as telephone, d.AREA_ID as areaId, d.DETAIL_ADDR as detailAddr, d.fax as fax, d.WEBSITE as website, d.PRINCINPAL as princinpal, d.PRINCINPAL_ID_CARD as princinpalIdCard, d.NATURE as nature, d.SHORT_NAME as shortName, d.PARENT_ID as parentId, d.PARENT_NAME as parentName, d.PROVINCE_ID as provinceId, d.CITY_ID as cityId, d.ORG_LEVEL as orgLevel, d.IS_DELETED as isDeleted, d.DEP_ID as depId, d.IS_ROOT as isRoot, d.EMAIL as email, a.NAME as provinceName from T_SES_OMS_ORG d left join T_SES_BMS_AREA a on d.province_id = a.id
where nvl(d.is_deleted,0)=0
and d.id= #{id}
and d.PARENT_ID= #{pid}
and nvl(d.IS_ROOT,'0')= 1
and d.TYPE_NAME= #{typeName}
and d.REQUIRE_DEP_ID= #{requireDepId}
and a.name like #{name}
and a.name not like #{notName}
and a.name like #{isName}
ORDER BY d.CREATED_AT DESC

