mapper.update:
    	 update T_SES_OMS_ORG s
	     set 
	         s.STATUS = #{status}
	     where s.ID=#{id}

mapper.select:
select d.id as id, d.NAME as name, d.TYPE_NAME as typeName, d.ADDRESS as address, d.MOBILE as mobile, d.POST_CODE as postCode, d.ORG_CODE as orgCode, d.TELEPHONE as telephone, d.AREA_ID as areaId, d.DETAIL_ADDR as detailAddr, d.fax as fax, d.WEBSITE as website, d.PRINCINPAL as princinpal, d.PRINCINPAL_ID_CARD as princinpalIdCard, d.NATURE as nature, d.SHORT_NAME as shortName, d.PARENT_ID as parentId, d.PARENT_NAME as parentName, d.PROVINCE_ID as provinceId, d.CITY_ID as cityId, d.ORG_LEVEL as orgLevel, d.IS_DELETED as isDeleted, d.DEP_ID as depId, d.IS_ROOT as isRoot, d.EMAIL as email, d.CONTACT_NAME as contactName, d.CONTACT_MOBILE as contactMobile, a.NAME as provinceName, d.Full_NAME as fullName from T_SES_OMS_ORG d left join T_SES_BMS_AREA a on d.province_id = a.id
where d.is_deleted=0
and d.id= #{id}
and d.PARENT_ID= #{pid}
and d.PARENT_ID is null
and d.TYPE_NAME= #{typeName}
and d.REQUIRE_DEP_ID= #{requireDepId}
and d.name like CONCAT(CONCAT('%', #{name}), '%')
and a.name not like CONCAT(CONCAT('%', #{notName}), '%')
and a.name like CONCAT(CONCAT('%', #{isName}), '%')
and d.PROVINCE_ID = #{provinceId}
and d.CITY_ID = #{cityId}
ORDER BY d.CREATED_AT DESC

SELECT count(1)
FROM T_SES_OMS_ORG
WHERE TYPE_NAME = #{typeName}
AND IS_DELETED = 0
AND PARENT_ID = #{pid}

	select 
 		 from T_SES_OMS_ORG 
        where  PARENT_ID=#{parentId} and POSITION>#{position}
