mapper.select:
select d.id as id, u.GENDER as gender, d.NATION as nation, d.PURCHASE_DEP_ID as purchaseDepId, d.POLITICAL as political, d.PURCAHSER_TYPE as purcahserType, d.BIRTH_AT as birthAt, d.AREA as area, d.PROVINCE_ID as provinceId, d.CITY_ID as cityId, d.TOWN_ID as townId, d.DETAIL_ADDR as detailAddr, d.ID_CARD as idCard, d.PROFESSIONAL as professional, d.TOP_STUDY as topStudy, d.GRADUATE_SCHOOL as graduateSchool, d.WORK_EXPERIENCE as workExperience, d.TRAIN_EXPERIENCE as trainExperience, d.QUA_CODE as quaCode, d.QUA_LEVEL as quaLevel, d.QUA_EDNDATE as quaEdndate, d.QUA_CERT as quaCert, d.QUA_RANK as quaRank, d.FAX as fax, u.ADDRESS as address, d.POST_CODE as postCode, d.QUA_START_DATE as quaStartDate, o.name as purchaseDepName, u.id as userId, u.REL_NAME as relName, d.AGE as age, u.DUTIES as duties, u.TELEPHONE as telephone from T_SES_OMS_PURCHASER d LEFT JOIN T_SES_OMS_PURCHASE_DEP p on d.PURCHASE_DEP_ID = p.id LEFT JOIN T_SES_OMS_ORG o on o.id = p.ORG_ID LEFT JOIN T_SES_BMS_USER u on d.id=u.TYPE_ID where nvl(d.is_deleted,0)=0
and d.id= #{id}
and d.PARENT_ID= #{pid}
and p.ID= #{purchaseDepId}
and u.REL_NAME= #{relName}
and d.ID_CARD= #{idCard}

mapper.update:
		 update T_SES_OMS_PURCHASER s
     set 
         UPDATED_AT=sysdate


	 		,NATION=#{nation,jdbcType=VARCHAR}


	 		,PURCHASE_DEP_ID=#{purchaseDepId,jdbcType=VARCHAR}


	 		,POLITICAL=#{political,jdbcType=VARCHAR}


	 		,PURCAHSER_TYPE=#{purcahserType,jdbcType=NUMERIC}


	 		,BIRTH_AT=#{birthAt,jdbcType=TIMESTAMP}


	 		,AREA=#{area,jdbcType=VARCHAR}


	 		,PROVINCE_ID=#{provinceId,jdbcType=VARCHAR}


	 		,CITY_ID=#{cityId,jdbcType=VARCHAR}


	 		,TOWN_ID=#{townId,jdbcType=VARCHAR}


	 		,DETAIL_ADDR=#{detailAddr,jdbcType=VARCHAR}


	 		,ID_CARD=#{idCard,jdbcType=VARCHAR}


	 		,PROFESSIONAL=#{professional,jdbcType=VARCHAR}


	 		,TOP_STUDY=#{topStudy,jdbcType=VARCHAR}


	 		,GRADUATE_SCHOOL=#{graduateSchool,jdbcType=VARCHAR}


	 		,WORK_EXPERIENCE=#{workExperience,jdbcType=VARCHAR}


	 		,TRAIN_EXPERIENCE=#{trainExperience,jdbcType=VARCHAR}


	 		,QUA_CODE=#{quaCode,jdbcType=VARCHAR}


	 		,QUA_LEVEL=#{quaLevel,jdbcType=VARCHAR}


	 		,QUA_EDNDATE=#{quaEdndate,jdbcType=TIMESTAMP}


	 		,QUA_CERT=#{quaCert,jdbcType=VARCHAR}


	 		,IS_DELETED=#{is_deleted,jdbcType=NUMERIC}


	 		,QUA_RANK=#{quaRank,jdbcType=VARCHAR}



	 		,FAX=#{fax,jdbcType=VARCHAR}


	 		,POST_CODE=#{postCode,jdbcType=VARCHAR}


	 		,QUA_START_DATE=#{isroot,jdbcType=TIMESTAMP}


	 		,AGE=#{age,jdbcType=NUMERIC}

     where s.id=#{id}
