  PACKAGE_ID, PROJECT_ID, SUPPLIER_ID
  )
values (#{id,jdbcType=VARCHAR}, #{createdAt,jdbcType=DATE}, #{quotePrice,jdbcType=DECIMAL}, 
  #{packageId,jdbcType=VARCHAR}, #{projectId,jdbcType=VARCHAR}, #{supplierId,jdbcType=VARCHAR}
  )
