mapper.select:
select wm_concat(t1.name) from( SELECT T2."NAME",T1.SUPPLIER_ID FROM "T_SES_SMS_SUPPLIER_TYPE_RELATE" t1 LEFT JOIN T_SES_SMS_SUPPLIER_TYPE_RELATE T3 ON T1.ID = T3.SUPPLIER_ID LEFT JOIN T_SES_BMS_DICTIONARY_DATA t2 on T1.SUPPLIER_TYPE_ID=T2.CODE ) t1 where 1=1
and T1.SUPPLIER_ID = #{id,jdbcType=VARCHAR}
GROUP BY T1.SUPPLIER_ID

select

from T_SES_SMS_SUPPLIER
where ID = #{id}

select

from T_SES_SMS_SUPPLIER
where ID = #{id}


select * from(
select t3.name||T2."NAME" as addressName,T1.* from
T_SES_SMS_SUPPLIER T1
LEFT JOIN T_SES_BMS_AREA T2 ON T1.ADDRESS=T2.ID
LEFT JOIN
T_SES_BMS_AREA T3 ON T2.PARENT_ID=T3.ID )
where 1=1 and
SUPPLIER_STATUS=5

	and SUPPLIER_STATUS = #{status}


	and SUPPLIER_NAME like '%${supplierName}%'


	AND CREATED_AT >= #{startDate,jdbcType=TIMESTAMP}


	AND CREATED_AT <= #{endDate,jdbcType=TIMESTAMP}


	AND CONTACT_NAME = #{contactName}


	and SUPPLIER_STATUS = #{status}


	and ADDRESS like '%${address}%'


	
		#{id}
	

ORDER BY FOUND_DATE DESC

mapper.update:
