mapper.select:
select

from T_SES_SMS_SUPPLIER where  (SUPPLIER_STATUS = 1 or SUPPLIER_STATUS = 5 or SUPPLIER_STATUS = 8)

	and SUPPLIER_NAME like '%${supplierName}%'


	
		#{id}

SELECT
FROM t_ses_sms_supplier WHERE IS_PROVISIONAL = '0'
and PROCUREMENT_DEP_ID = #{procurementDepId}
and (SUPPLIER_STATUS = 0 or SUPPLIER_STATUS = 1 or SUPPLIER_STATUS = 2 or SUPPLIER_STATUS = 3)
and (SUPPLIER_STATUS = 4 or SUPPLIER_STATUS = 5 or SUPPLIER_STATUS = 6)
and (SUPPLIER_STATUS = 5 or SUPPLIER_STATUS = 7 or SUPPLIER_STATUS = 8)
AND SUPPLIER_NAME like CONCAT(CONCAT('%', #{supplierName}), '%')
AND SUPPLIER_STATUS = #{status}
AND BUSINESS_TYPE = #{businessType}
ORDER BY AUDIT_DATE DESC NULLS LAST

SELECT * FROM ( SELECT "NVL" (T4. NAME, T3. NAME) AS NAME, t1.typeId, t2.* FROM ( SELECT ID, wm_concat (DISTINCT(typeId)) AS typeId FROM ( SELECT T1.*, t3.SUPPLIER_TYPE_ID AS typeId FROM T_SES_SMS_SUPPLIER t1 LEFT JOIN T_SES_SMS_SUPPLIER_TYPE_RELATE t3 ON t1. ID = t3.SUPPLIER_ID ) GROUP BY ID ) t1 LEFT JOIN T_SES_SMS_SUPPLIER t2 ON t1. ID = t2. ID LEFT JOIN T_SES_BMS_AREA T3 ON T2.ADDRESS = T3. ID LEFT JOIN T_SES_BMS_AREA T4 ON T3.PARENT_ID = T4. ID
RIGHT JOIN ( select supplier_id as id from T_SES_SMS_SUPPLIER_ITEM where category_id in (
#{item}
) ) t5 on t1.ID = t5.ID
) WHERE 1 = 1
and SUPPLIER_NAME like '%${supplierName}%'
AND CREATED_AT >= #{startDate}
AND CREATED_AT <= #{endDate}
AND CONTACT_NAME like '%${contactName}%'
AND BUSINESS_TYPE =#{businessType}
and IS_PROVISIONAL = '0'
and SUPPLIER_STATUS = #{status}
and (SUPPLIER_STATUS = 1 or SUPPLIER_STATUS = 4 or SUPPLIER_STATUS = 5 or SUPPLIER_STATUS = 6 or SUPPLIER_STATUS =7 or SUPPLIER_STATUS = 8 or SUPPLIER_STATUS =9)
and NAME like '%${address}%'
and LOGIN_NAME like '%${loginName}%'
and MOBILE like '%${mobile}%'
and IS_PROVISIONAL = #{isProvisional}
and "INSTR"(typeId, #{itemType})>0
and SCORE >= #{scoreStart}
and SCORE < #{scoreEnd}
ORDER BY CREATED_AT DESC NULLS LAST
ORDER BY AUDIT_DATE DESC NULLS LAST

select * from( select t1.*, t2.ID as SUPPLIER_RELATE_ID, t2.SUPPLIER_ID, t2.SUPPLIER_TYPE_ID as SUPPLIER_TYPE_ID_, t2.CREATED_AT as CREATED_AT_, t2.UPDATED_AT as UPDATED_AT_, t3.NAME as SUPPLIER_TYPE_NAME from T_SES_SMS_SUPPLIER t1 left join T_SES_SMS_SUPPLIER_TYPE_RELATE t2 on t1. ID = t2.SUPPLIER_ID left join T_SES_SMS_SUPPLIER_TYPE t3 on t2.SUPPLIER_TYPE_ID = t3. ID ) t4 where 1=1
and t4.SUPPLIER_NAME like '%${supplierName}%'
AND t4.CREATED_AT >= #{startDate,jdbcType=TIMESTAMP}
AND t4.CREATED_AT <= #{endDate,jdbcType=TIMESTAMP}
AND t4.CONTACT_NAME = #{contactName}
and t4.SUPPLIER_STATUS = #{status}
and t4.ADDRESS like '%${address}%'
AND t4.SUPPLIER_TYPE_NAME like #{supplierType}
ORDER BY FOUND_DATE DESC

