mapper.select:
select * from( select t1.typeId,t1.categoryId,t2.* from( select id,wm_concat(DISTINCT(typeId)) as typeId ,min(CATEGORYID) as categoryId from ( SELECT T1.*,t2.CATEGORY_ID as categoryId ,t3.SUPPLIER_TYPE_ID as typeId FROM "T_SES_SMS_SUPPLIER" t1 left join T_SES_SMS_SUPPLIER_TYPE_RELATE t3 on t1. ID = t3.SUPPLIER_ID LEFT JOIN ( SELECT T1. ID, CATEGORY_ID FROM "T_SES_SMS_SUPPLIER" t1 RIGHT JOIN T_SES_SMS_SUPPLIER_ITEM t2 ON T1. ID = T2.SUPPLIER_ID ) t2 ON t1.id =t2.id ) GROUP BY id )t1 left JOIN T_SES_SMS_SUPPLIER t2 on t1.id=t2.id ) where 1=1 AND IS_PROVISIONAL = 0 and ( SUPPLIER_STATUS = 1 or SUPPLIER_STATUS = 5 or SUPPLIER_STATUS = 7 )
AND ADDRESS = #{addressId}
AND
ADDRESS = #{split}
AND
"INSTR"(typeId, #{type})>0
AND (
"INSTR"(categoryId, #{category})>0
)
AND (
"INSTR"(categoryId, #{category})>0
)
ORDER BY FOUND_DATE DESC

