mapper.select:
SELECT sup.ID, sup.SUPPLIER_NAME, sup.SUBMIT_AT, sup.INVES_AT, sup.INVES_PEOPLE, sup.SUPPLIER_STATUS, sup.AUDIT_TEMPORARY, subQ.SUPPLIER_TYPE_IDS, subQ.SUPPLIER_TYPE_NAMES, subQ.ADDRESS_NAMES, dic.NAME AS BUSINESS_NATURE FROM( SELECT FROM T_SES_SMS_SUPPLIER WHERE IS_DELETED = 0 AND SUPPLIER_STATUS IN(5,7,8) AND ID IN( SELECT * FROM( SELECT pass.SUPPLIER_ID FROM T_BSS_PPMS_SUPPLIER_CHECK_PASS pass LEFT JOIN T_BSS_PPMS_PROJECTS proj ON pass.PROJECT_ID = proj.ID WHERE pass.IS_WON_BID = 1
AND proj.PURCHASE_DEP_ID = #{orgId}
ORDER BY pass.CONFIRM_TIME ASC) )) sup LEFT JOIN (SELECT ID, wm_concat(DISTINCT(SUPPLIER_TYPE_ID)) AS SUPPLIER_TYPE_IDS, wm_concat(DISTINCT(SUPPLIER_TYPE_NAME)) AS SUPPLIER_TYPE_NAMES, wm_concat(PARENT_AREA_NAME || AREA_NAME) AS ADDRESS_NAMES FROM( SELECT sup.ID, sr.SUPPLIER_TYPE_ID, dic.NAME AS SUPPLIER_TYPE_NAME, pArea.NAME AS PARENT_AREA_NAME, area.NAME AS AREA_NAME FROM T_SES_SMS_SUPPLIER sup LEFT JOIN T_SES_SMS_SUPPLIER_TYPE_RELATE sr ON sup.ID = sr.SUPPLIER_ID LEFT JOIN T_SES_BMS_DICTIONARY_DATA dic ON sr.SUPPLIER_TYPE_ID = dic.CODE LEFT JOIN T_SES_SMS_SUPPLIER_ADDRESS addr ON sup.ID = addr.SUPPLIER_ID LEFT JOIN T_SES_BMS_AREA area ON area.ID = addr.ADDRESS LEFT JOIN T_SES_BMS_AREA pArea ON area.PARENT_ID = pArea.ID ) GROUP BY ID) subQ ON subQ.ID = sup.ID LEFT JOIN T_SES_BMS_DICTIONARY_DATA dic ON sup.BUSINESS_NATURE = dic.ID WHERE 1 = 1
AND sup.SUPPLIER_NAME LIKE CONCAT(CONCAT('%',#{supplierName}), '%')
AND sup.SUPPLIER_STATUS = #{status}
AND sup.AUDIT_TEMPORARY = #{auditTemporary}
AND sup.BUSINESS_NATURE = #{businessNature}
AND to_char(sup.INVES_AT,'yyyy-mm-dd') = to_char(#{invesAt},'yyyy-mm-dd')
AND subQ.ADDRESS_NAMES LIKE CONCAT(CONCAT('%', #{addressName}), '%')
