mapper.update:
UPDATE T_SYSTEM_SMS_RECORD
SET IS_DELETED = 1
WHERE ID = #{id,jdbcType=VARCHAR}

mapper.select:
    select 

    FROM T_SYSTEM_SMS_RECORD
    where IS_DELETED = 0


        AND SEND_LINK = #{sendLink}



        AND OPERATOR IN 

            #{item}  




        AND RECIPIENT IN 

            #{item}  




        AND RECEIVE_NUMBER LIKE CONCAT(CONCAT('%', #{receiveNumber}), '%')




			AND STATUS = #{status}


			AND STATUS IN (1,2)




        AND to_char(SEND_TIME,'yyyy-MM-dd HH:mm:ss') >= #{startTime,jdbcType=VARCHAR}


        AND to_char(SEND_TIME,'yyyy-MM-dd HH:mm:ss') <= #{endTime,jdbcType=VARCHAR} 


        AND ORG_ID = #{orgId}


        AND OPERATOR = #{loginUser}

      ORDER BY SEND_TIME DESC,ID ASC

mapper.update:
UPDATE T_SYSTEM_SMS_RECORD
SET STATUS = #{status}, FAIL_REASON = #{failReason}, UPDATED_AT = #{updatedAt}
WHERE MSG_ID = #{msgId}

    AND RECEIVE_NUMBER = #{receiveNumber}
